"use strict";(self.webpackChunkarchive_dernier_verre=self.webpackChunkarchive_dernier_verre||[]).push([[7344],{92673:(e,n,i)=>{i.r(n),i.d(n,{assets:()=>o,contentTitle:()=>l,default:()=>d,frontMatter:()=>s,metadata:()=>t,toc:()=>u});var r=i(74848),a=i(28453);const s={title:"2023-02-19 - Balcon (Astrea, Camille, Oka, William)",toc_min_heading_level:4,toc_max_heading_level:4,tags:["tag_AstreaHichtra","tag_CamilleReza","tag_Oka","tag_WilliamDyers"]},l="Balcon (Astrea, Camille, Oka, William)",t={id:"Archives/Events/SaintValentin2023/Balcon2_Astrea_Camille_Oka_William",title:"2023-02-19 - Balcon (Astrea, Camille, Oka, William)",description:"Balcon - 19/02/2023",source:"@site/docs/Archives/Events/SaintValentin2023/Balcon2_Astrea_Camille_Oka_William.mdx",sourceDirName:"Archives/Events/SaintValentin2023",slug:"/Archives/Events/SaintValentin2023/Balcon2_Astrea_Camille_Oka_William",permalink:"/ArchiveLeDernierVerre/docs/Archives/Events/SaintValentin2023/Balcon2_Astrea_Camille_Oka_William",draft:!1,unlisted:!1,tags:[{inline:!1,label:"Astrea Hichtra",permalink:"/ArchiveLeDernierVerre/docs/tags/AstreaHichtra",description:"Archives o\xf9 Astrea Hichtra intervient"},{inline:!1,label:"Camille Reza",permalink:"/ArchiveLeDernierVerre/docs/tags/CamilleReza",description:"Archives o\xf9 Camille Reza intervient"},{inline:!1,label:"Oka",permalink:"/ArchiveLeDernierVerre/docs/tags/Oka",description:"Archives o\xf9 Oka intervient"},{inline:!1,label:"William Dyers",permalink:"/ArchiveLeDernierVerre/docs/tags/WilliamDyers",description:"Archives o\xf9 William Dyers intervient"}],version:"current",frontMatter:{title:"2023-02-19 - Balcon (Astrea, Camille, Oka, William)",toc_min_heading_level:4,toc_max_heading_level:4,tags:["tag_AstreaHichtra","tag_CamilleReza","tag_Oka","tag_WilliamDyers"]}},o={},u=[{value:"Balcon - 19/02/2023",id:"balcon---19022023",level:3},{value:"Camille Reza",id:"camille-reza",level:4},{value:"Astrea Hichtra ~ Mafia Iranienne",id:"astrea-hichtra--mafia-iranienne",level:4},{value:"Camille Reza",id:"camille-reza-1",level:4},{value:"Astrea Hichtra ~ Mafia Iranienne",id:"astrea-hichtra--mafia-iranienne-1",level:4},{value:"Camille Reza",id:"camille-reza-2",level:4},{value:"Astrea Hichtra ~ Mafia Iranienne",id:"astrea-hichtra--mafia-iranienne-2",level:4},{value:"Camille Reza",id:"camille-reza-3",level:4},{value:"Astrea Hichtra ~ Mafia Iranienne",id:"astrea-hichtra--mafia-iranienne-3",level:4},{value:"William Dyers",id:"william-dyers",level:4},{value:"Oka",id:"oka",level:4},{value:"Astrea Hichtra ~ Mafia Iranienne",id:"astrea-hichtra--mafia-iranienne-4",level:4},{value:"William Dyers",id:"william-dyers-1",level:4},{value:"Camille Reza",id:"camille-reza-4",level:4},{value:"William Dyers",id:"william-dyers-2",level:4},{value:"Astrea Hichtra ~ Mafia Iranienne",id:"astrea-hichtra--mafia-iranienne-5",level:4},{value:"William Dyers",id:"william-dyers-3",level:4},{value:"Astrea Hichtra ~ Mafia Iranienne",id:"astrea-hichtra--mafia-iranienne-6",level:4},{value:"William Dyers",id:"william-dyers-4",level:4}];function c(e){const n={admonition:"admonition",blockquote:"blockquote",em:"em",h1:"h1",h3:"h3",h4:"h4",header:"header",hr:"hr",li:"li",p:"p",strong:"strong",ul:"ul",...(0,a.R)(),...e.components};return(0,r.jsxs)(r.Fragment,{children:[(0,r.jsx)(n.header,{children:(0,r.jsx)(n.h1,{id:"balcon-astrea-camille-oka-william",children:"Balcon (Astrea, Camille, Oka, William)"})}),"\n",(0,r.jsx)(n.admonition,{title:"R\xe9ception de Saint Valentin 2023",type:"info",children:(0,r.jsx)(n.h3,{id:"balcon---19022023",children:"Balcon - 19/02/2023"})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"camille-reza",children:"Camille Reza"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"19-02-2023 16:48:07"}),"\n"]}),"\n"]}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.em,{children:(0,r.jsx)(n.strong,{children:"Voil\xe0 que notre mari\xe9 daignait enfin montrer le bout de son nez ! V\xeatu de son impeccable costume gris et noir qu'il ne semble jamais quitter, notre homme \xe9tait en plus de \xe7a en excellente compagnie. \xc0 ses c\xf4t\xe9s se tenaient la seconde vedette de cette somptueuse r\xe9ception : Astrea Hichtra, futur \xe9pouse du Satrape de Suse. Ce dernier d\xe9ambulait \xe0 travers les all\xe9es fastueuse du manoir, un verre de rouge \xe0 la main. Il avait personnellement valid\xe9 la liste des breuvages mis \xe0 disposition pour l'\xe9v\xe8nement. Apr\xe8s tout, Camille ne tenait pas \xe0 ce que les convives s'abreuvent de gn\xf4le bon march\xe9."})})}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Dyers a toujours eu un don pour organiser des \xe9v\xe8nements, tu ne trouve pas ? J'aurai trouv\xe9 plus judicieux d'un peu plus d\xe9v\xeatir les esclaves, mais rien n'est jamais parfait !"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"astrea-hichtra--mafia-iranienne",children:"Astrea Hichtra ~ Mafia Iranienne"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"19-02-2023 17:03:22"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"De son c\xf4t\xe9, la mari\xe9 s'\xe9tait v\xeatu d'un magnifique robe blanche, tr\xe8s princesse d'ailleurs. Restant au c\xf4t\xe9 de son futur \xe9poux, m\xeame ses lions \xe9taient pr\xe9sent. De toute fa\xe7on elle ne se d\xe9pla\xe7aient jamais sans eux. Dans une de ses mains 654 (Merci Spartacus) elle avait aussi un verre de rouge alors qu'elle observait les d\xe9corations et les convives. Bien, cette femme lapin avait respect\xe9 chacun de ses souhaits."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Tu as raison oui. Je suis d'ailleurs surprise qu'il ne soit toujours pas venu \xe0 notre rencontre."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"camille-reza-1",children:"Camille Reza"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"19-02-2023 17:20:45"}),"\n"]}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"\xc7a ne tardera point \xe0 mon avis, il doit juste \xeatre un peu occup\xe9."}),"\n"]}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.em,{children:(0,r.jsx)(n.strong,{children:"L'homme se posa sur une rembarde en pierre magnifiquement sculpt\xe9e. Son regard s'\xe9garait dans l'horizon, le balcon offrait une vue tout bonnement splendide sur la ville."})})}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Qui plus est, nous sommes en plut\xf4t bon termes avec le Dernier Verre. J'y pense : aurais tu une id\xe9e de ce que l'on pourrait lui adresser comme dol\xe9ance ? Nous avons toujours le droit \xe0 un b\xe2timent, un march\xe9, un service ou quelque chose du genre."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"astrea-hichtra--mafia-iranienne-1",children:"Astrea Hichtra ~ Mafia Iranienne"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"19-02-2023 18:11:01"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Buvant une gorg\xe9 de son vin, elle caressa de sa main libre la t\xeate d'un de ses lions tout en observant \xe0 son tour le paysage. Ses cheveux parfaitement boucl\xe9s pour l'occasion volait l\xe9g\xe8rement avec la brise qu'il y avait. A la question qu'il lui posa, elle r\xe9fl\xe9chira quelques instant. La mafia avait d\xe9j\xe0 r\xe9cup\xe9rer l'h\xf4pital, poss\xe9dait une partie de la march\xe9 de la prostitution, que prendre ensuite ?"})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Que dirais tu de l'armurerie situ\xe9 en ville ? Cela pourrais agrandir notre march\xe9  et nous gagnerons en puissance par rapport aux autres mafia"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"camille-reza-2",children:"Camille Reza"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"19-02-2023 19:27:52"}),"\n"]}),"\n"]}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.em,{children:(0,r.jsx)(n.strong,{children:"L'homme hocha la t\xeate. C'\xe9tait une plut\xf4t bonne id\xe9e qu'elle venait de lui proposer."})})}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Ma foi, oui c'est un choix judicieux. Nous pourvoir en armes demeure bien loin d'\xeatre une t\xe2che compliqu\xe9e, mais au moins nous rendrons cette besogne plus compliqu\xe9e pour les organisations rivales. Je soumettrais \xe7a \xe0 Dyers."}),"\n"]}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.em,{children:(0,r.jsx)(n.strong,{children:"Camille but une bonne lamp\xe9e dans son verre. Grossi\xe8re erreur. Il sentit sa gorge lui piquer tr\xe8s m\xe9chamment. Le Satrape tenait mal l'alcool."})})}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Uuh... C'est \xe0 quelle heure la d\xe9claration officielle d\xe9j\xe0 ?"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"astrea-hichtra--mafia-iranienne-2",children:"Astrea Hichtra ~ Mafia Iranienne"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"19-02-2023 19:45:55"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Heureuse que sa proposition plaise \xe0 son futur \xe9poux, elle viendra boire une nouvelle gorg\xe9. De son c\xf4t\xe9 elle tenait bien l'alcool, et dans tout les cas il \xe9tait hors de question qu'elle se retrouve \xe9m\xe9ch\xe9 pour un tel \xe9v\xe8nements."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Dans peut de temps. Pourquoi, tu as h\xe2tes de montrer \xe0 tout ce bas peuples pour qui ils sont venues ?"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"camille-reza-3",children:"Camille Reza"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"20-02-2023 23:17:31"}),"\n"]}),"\n"]}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.em,{children:(0,r.jsx)(n.strong,{children:"L'homme d\xe9posa son verre sur la rambarde. Mieux valait qu'il cesse de siroter pour un petit temps."})})}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Non seulement. Mais surtout pour enfin officialiser tout \xe7a. Je t'avoue que les longues r\xe9ception o\xf9 je ne peux pas assouvir mes envies moins... Socialement ad\xe9quate, disons. Tu as des nouvelles d'Asteria d'ailleurs ? Je pense que c'\xe9tait une bonne id\xe9e de l'amener par ailleurs, le peuple aura enfin l'occasion de constater que nul ne poss\xe8de une esclave plus charmante."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"astrea-hichtra--mafia-iranienne-3",children:"Astrea Hichtra ~ Mafia Iranienne"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"21-02-2023 14:02:15"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Vahakn, l'un des deux lions, viendra marcher vers le Satrape et frotter une fois sa t\xeate contre sa jambe, r\xe9clamant une caresse. Des deux lions c'\xe9tait celui qui s'\xe9tait le plus attacher \xe0 son second propri\xe9taire. Observant la sc\xe8ne avec un petit sourire, Astrea regarda ensuite le paysage que lui offrait le balcon."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Pas pour le moment non, ne pas l'avoir \xe0 tes genoux te manque d\xe9j\xe0 ? Par ailleurs je n'ai toujours pas croiser Mr Dyers ainsi que la femme que j'ai vu pour l'organisation."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"A son tour elle s'approcha de lui pour s'accouder \xe0 la rembarde, malgr\xe9 sa robe l\xe9g\xe8re elle n'avait pas froid. Regardant ensuite son verre de rouge, elle en reprit une gorg\xe9 avant de poser son verre."})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"william-dyers",children:"William Dyers"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"22-02-2023 13:08:27"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Suivant les indications de mes employ\xe9s travaillant \xe0 ce que la r\xe9ception se d\xe9roule bien, je me retrouve sur le balcon habill\xe9 de mon plus beau costume blanc. J'ai dans mes bras un cadeau qui fait environ 50x50x150 empaquet\xe9 avec un papier cadeau brillant donnant une texture dor\xe9e \xe0 l'objet."})}),"\n",(0,r.jsx)("img",{src:"/ArchiveLeDernierVerre/img/Event/22022023_Balcon.png",alt:"Tenue",style:{width:"250px",objectFit:"cover",border:"1px solid #000",display:"block",marginLeft:"auto",marginRight:"auto"}}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Je n'ai pas \xe0 chercher bien longtemps pour retrouver le couple pr\xe8s du bord de la rambarde. Difficile de les rater avec les deux lions de la jeune femme qui participent certainement \xe0 l'isolement dans lequel ils se retrouvent malgr\xe9 la r\xe9ception en leur honneur. En bon g\xe9rant du Dernier Verre, je fais totalement fi de ces animaux impressionnant et m'approche calmement, le regard fix\xe9 sur Camille Reza."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Bonjour Monsieur Reza. Tr\xe8s belle r\xe9ception si je peux me permettre. Mademoiselle, tr\xe8s heureux de vous rencontrer. Je suis William Dyers, humble propri\xe9taire du Dernier Verre et votre oblig\xe9 en cette journ\xe9e si sp\xe9ciale."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Je fais un petit signe de salut de la t\xeate \xe0 l'homme et tend la main \xe0 la jeune femme, proposant un baisemain qui me semble appropri\xe9, bien que je puisse comprendre son refus si refus il y a."})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"oka",children:"Oka"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"22-02-2023 13:51:15"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.em,{children:"Oka avait l\xe9g\xe8rement d\xe9vier du balcon num\xe9ro 1, effectivement il ne faisait que des tours. Arriv\xe9 ici, il avait toujours sa cigarette aux l\xe8vres ainsi que son verre personnel \xe0 la main. Observant les gens non pas de haut mais de ses 2m et quelques, il fit un l\xe9ger sourire en apercevant le directeur du dernier verre, ainsi que le g\xe9rant de la ville. S'approchant de lui, il lui posa une main sur l'\xe9paule, une main d\xe9licate \xe0 la sensation glaciale, reconnaissable entre toutes. Comment un ancien de Langdorf pourrait-il ne pas reconna\xeetre Will? Ce bon vieux will... Mais en voyant la sc\xe8ne du baise-main,il pr\xe9f\xe9ra se mettre sur le c\xf4t\xe9 en laissant place \xe0 la galanterie."})}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Messieurs dames, Bonsoir."}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"astrea-hichtra--mafia-iranienne-4",children:"Astrea Hichtra ~ Mafia Iranienne"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"22-02-2023 14:12:10"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"C'est Astghik qui viendra \xe0 remarquer en premier la pr\xe9sence des deux intrus, suivit de peut par Vahakn. Astrea se tournant \xe0 ce moment, fit un petit bruit de talons sur le sol pour que ses deux fauves s'assirent, encadrant les futurs mari\xe9s. Elle reconnu sans mal Dyers, n\xe9anmoins l'autre .. Un serveur ? Un simple coup d'\u0153il sur Astghik lui fit comprendre que c'\xe9tait surement un homme puissant. Sixi\xe8me sens animal que voulez vous. Regardant alors les deux hommes avec son sourire, elle viendra \xe0 poser sa main dans celle de William, acceptant son baise main."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Allons Mr Dyers, c'est \xe0 votre assistante Mme Levenski que nous devons cette r\xe9ception, elle a suivit \xe0 la lettre chacune de mes directives"}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Et heureusement pour elle. Ses fauves s'en \xe9taient l\xe9ger les babines quand elle s'\xe9tait entretenu avec Oria pour les pr\xe9paratifs. Un seul faux pas de sa part et c'\xe9tait sur une chaise de Judas qu'elle aurait continuer de travailler au Dernier Verre."})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"william-dyers-1",children:"William Dyers"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"22-02-2023 15:59:51"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Attentif aux deux b\xeates qui nous entourent, je ne manque pas de remarquer le pas derri\xe8re moi qui s'approche. Les vieux r\xe9flexes de mercenaires qui me forcent \xe0 rester tr\xe8s attentifs \xe0 mon environnement et \xe0 voir toutes les issues de secours d\xe8s que j'arrive dans une pi\xe8ce, que voulez vous. Aussi, je parviens \xe0 retenir mon tressaillement en sentant la large main d'Oka sur mon \xe9paule, lui accordant juste un bref regard."})}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"J'accorde la priorit\xe9 au salut de la demoiselle qui m'offre sa main, me permettant ainsi de lui rendre honneur comme il se doit. Elle complimente ma cavali\xe8re pour son travail, ce qui ne manque pas de me faire sourire alors que je rel\xe2che sa main en me redressant."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Je dois reconnaitre que, pour cette r\xe9ception, vous profitez des services des gens les plus talentueux de Langdorf et esp\xe8re qu'ils vous apportent pleine satisfaction. Je vous pr\xe9sente d'ailleurs M. Oka, chef de mafia et responsable de la salle tout au long de la r\xe9ception. C'est lui qui coordonne toutes mes \xe9quipes aujourd'hui."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Les pr\xe9sentations faites, je laisse Oka saluer la jeune femme et en profite pour aller vers Camille, posant mon pr\xe9sent sur la rambarde en pierre devant lui. L'objet est encombrant et un peu lourd mais je prend bien soin de le poser d\xe9licatement."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"M. Reza, en ce jour si sp\xe9cial pour vous, je me suis permis de vous apporter ce cadeau. En signe de notre amiti\xe9 et de nos \xe9changes professionnels fructueux."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:"||Le cadeau contient une boite en bois noble, cisel\xe9e de motif perse sur toute sa longueur puis vernis. Elle est ferm\xe9e par deux simples loquet et contient une \xe9p\xe9e perse, plus exactement un shamshir d'honneur. La lame pr\xe9sente un aspect sombre qui contraste avec la dorure de la garde. Une inscription votive court sur toute la lame. Elle est plate et se termine par une pointe en \"langue de carpe\".  Le fourr\xe9 est dor\xe9 et porte 7 turquoises faisant \xe0 la fois r\xe9f\xe9rence aux 7 sabres du proph\xe8te ainsi qu'aux lectures du coran||"}),"\n",(0,r.jsx)("img",{src:"/ArchiveLeDernierVerre/img/Event/22022023_Balcon_2.png",alt:"Cadeau",style:{width:"250px",objectFit:"cover",border:"1px solid #000",display:"block",marginLeft:"auto",marginRight:"auto"}}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"camille-reza-4",children:"Camille Reza"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"22-02-2023 22:15:26"}),"\n"]}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Et bien, quand on parle du loup."}),"\n"]}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.em,{children:(0,r.jsx)(n.strong,{children:"Comme la coutume l'exige, Camille adressa une petite r\xe9v\xe9rence au patron du Dernier Verre. Non pas pour d\xe9montrer sa soumission mais pour exprimer un certains respect \xe0 l'\xe9gard de Dyers."})})}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Mes salutations monsieur Oka, je suis ravi de vous rencontrer. J'esp\xe8re que nous aurons l'occasion de faire plus ample connaissance \xe0 l'avenir."}),"\n"]}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.em,{children:(0,r.jsx)(n.strong,{children:"Remerciant William d'un geste de t\xeate, Camille ouvrit d\xe9licatement l'imposante bo\xeete. L'emballage du pr\xe9sent constituait d\xe9j\xe0 une formidable pi\xe8ce de d\xe9coration. Mais le contenu de cette male d\xe9passait toutes ses esp\xe9rances, en t\xe9moigne son regard \xe9merveill\xe9. Il effleura du bout des doigts la lame du Shashmir : jamais il n'a eu l'occasion de toucher une telle merveille. Le Satrape prit l'arme en main, il ex\xe9cuta dans le vide quelques mouvements de taille. Rien \xe0 dire : le poids du sabre demeurait parfaitement \xe9quilibr\xe9e."})})}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Hehe... Je ne saurai comment vous remercier, monsieur Dyers ! Cela doit \xeatre grisant de tuer avec un tel bijou, n'est ce pas ?"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"william-dyers-2",children:"William Dyers"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"22-02-2023 22:47:54"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Restant en retrait, les mains dans le dos, je laisse l'homme d\xe9couvrir mon cadeau. L'illumination dans son regard quand il d\xe9couvre la pi\xe8ce me fait sourire mais je reste droit \xe0 c\xf4t\xe9 de lui, le laissant sortir l'arme de son \xe9tui pour quelques mouvements habiles avec celle-ci. Un simple petit mouvement de la t\xeate pour accepter ses remerciements cache une immense satisfaction de lui avoir fait plaisir dans je reste dans la retenue."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"J'ai tendance \xe0 pr\xe9f\xe9rer les lames courtes mais je dois reconnaitre que vous maniez terriblement bien cette arme. Ravi qu'elle vous plaise."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Je tourne le regard vers la future mari\xe9e encore aux prises avec Oka avec un petit sourire."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Bien \xe9videmment, j'ai aussi quelque chose pour votre \xe9pouse mais je n'ai pas trouv\xe9 de moyen de l'emballer correctement. Je me vois donc navrer d'avoir \xe0 vous l'offrir sans la joie de pouvoir ouvrir le paquet. Si M. Reza me l'accorde, bien entendu."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"astrea-hichtra--mafia-iranienne-5",children:"Astrea Hichtra ~ Mafia Iranienne"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"23-02-2023 16:30:02"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Un chef de mafia alors ce .. Oka. Elle avait bien entendu son nom une fois ou deux mais ne l'avait pas retenue pour autant. Mais elle comprenait mieux maintenant pourquoi Astghik montrait l\xe9g\xe8rement les crocs sans pour autant grogner. Vahakn lui semblait plus int\xe9resser par le coffret qu'avait apporter William. Restant proche de Camille, il plaqua l\xe9g\xe8rement ses oreilles en arri\xe8re quand le Satrape viendra \xe0 sortir de l'\xe9crin une lame bien particuli\xe8re. Astrea observa a son tour son futur \xe9poux mani\xe9 sans aucune difficult\xe9 le pr\xe9sent qu'on venait de lui faire. En plus de ses mani\xe8res, Dyers savait comment faire plaisir, reste a voir ce qu'il avait pr\xe9vu pour la lionne."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Il me tarde de te voir l'utiliser Camille, je suis sur que tu trouveras un gueux pour \xe7a."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:'Malgr\xe9 sa voix douce et son sourire ang\xe9lique, ses paroles montraient bien que Astrea se sentait sup\xe9rieur aux autres. C\'\xe9tait une "princesse" que voulez vous. Une "princesse" qui se d\xe9lectait des visages meurtries des jouets qu\'elle torturait. Enfin, la n\'est pas le th\xe8me de la soir\xe9e, il serait dommage de choquer les convives.'})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Vous attisez ma curiosit\xe9 Mr Dyers, je me demande de quoi il s'agit"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"william-dyers-3",children:"William Dyers"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"25-02-2023 18:42:44"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Obnubil\xe9 par son nouveau jouet, j'ai l'impression que Camille ne m'a pas entendu, ou plut\xf4t qu'il ne m'a pas \xe9cout\xe9. Je prend son absence de r\xe9ponse pour un oui, \xe9tant donn\xe9 que sa future femme semble int\xe9ress\xe9 par le cadeau que je lui r\xe9serve. Je laisse donc l'homme continuer \xe0 agiter son \xe9p\xe9e dans les airs et \xe0 la d\xe9tailler du regard et m'approche de la jeune fille, gardant mes mains dans mon dos."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Vous concernant madame, j'ai d\xfb choisir sans avoir eut la chance de vous rencontrer. Je me suis donc fi\xe9 \xe0 ce que votre entourage a bien voulu me dire et esp\xe8re ne pas faire doublon avec un autre cadeau que vous auriez re\xe7u."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Je sors mon t\xe9l\xe9phone, un vieux mod\xe8le a clapet sur lequel je pianote avant de le ranger, reprenant ma pose, mains dans le dos. Je m'approche de la rambarde, \xe0 une distance raisonnable de la jeune fille du c\xf4t\xe9 oppos\xe9 \xe0 son lion. Je la regarde, lui adresse un grand sourire pour la faire patienter un petit instant, son mari continuant \xe0 faire des moulinets dans le vide en fond. Puis j'entends le bruit que j'attendais et viens placer ma main droite devant ma bouche."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Vous devriez observer le jardin, c'est par l\xe0 qu'il va venir."}),"\n"]}),"\n",(0,r.jsxs)(n.p,{children:["||",(0,r.jsx)(n.strong,{children:"Reportant mon regard au loin, je met deux doigts dans ma bouche pour siffler aussi fort que je peux. S'en suit un galop au loin, un galop rapide mais qui se rapproche. Parmi la v\xe9g\xe9tation en bordure de propri\xe9t\xe9, un cheval de robe bai sort et se met \xe0 galoper dans notre direction. Je le vois et le pointe du doigt \xe0 l'int\xe9ress\xe9e pour qu'elle ne le rate pas. Le cheval ralentit \xe0 proximit\xe9 de la route jusqu'\xe0 s'arr\xeater pour brouter un peu d'herbe, provoquant nombre de \"oh\" de surprise parmi les invit\xe9s encore \xe0 l'entr\xe9e de la propri\xe9t\xe9."}),"||"]}),"\n",(0,r.jsx)(n.p,{children:"||- Pur-sang arabe. Je me suis dit que compte tenu de votre \xe9ducation, vous deviez appr\xe9cier l'\xe9quitation. C'est une femelle, elle s'appelle \"Kissme\". Si vous souhaitez la monter, j'ai aussi fait venir une selle et tout le mat\xe9riel n\xe9cessaire.||"}),"\n",(0,r.jsx)("img",{src:"/ArchiveLeDernierVerre/img/Event/25022023_Balcon.png",alt:"Cadeau",style:{width:"250px",objectFit:"cover",border:"1px solid #000",display:"block",marginLeft:"auto",marginRight:"auto"}}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"astrea-hichtra--mafia-iranienne-6",children:"Astrea Hichtra ~ Mafia Iranienne"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"25-02-2023 21:49:00"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Voyant alors son futur \xe9poux faire mumuse avec son \xe9p\xe9e, elle fut quelque peu vex\xe9e que celui ci l'ignore compl\xe8tement, malgr\xe9 tout elle ne le montra pas. Reportant alors son regard sur William, elle l'\xe9couta avant de hausser un de ses sourcils parfaits. Il est vrai que cela serait dommage qu'il ai fait un doublon, m\xeame si il y avait peu de chance. Observant chacun de ses fait et geste, elle finira par tourner la t\xeate vers le jardin quand il en fera de m\xeame. Vraiment ce qu'il lui avait choisi l'intriguait de plus en plus. Astghik, le lion \xe0 c\xf4t\xe9 de la mari\xe9, viendra \xe0 se redresser avant de se lever pour poser ses deux \xe9normes pattes sur la rembarde du balcon. D'une main elle viendra caresser sa crini\xe8re avant d'entendre ce que lui avait entendu en premier, les bruit de sabot martelant le sol."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Non Astghik ce n'est pas un repas pour toi"}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Rigolant l\xe9g\xe8rement, elle regarda le pur sang passer tranquillement du galop au pas avant de brouter l'herbe \xe0 proximit\xe9. De loin elle pouvait d\xe9j\xe0 voir qu'elle avait des bon aplomb, \xe0 voir comme elle serait niveau comportement et surtout si elle n'\xe9tait pas craintif."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Et bien, voil\xe0 une belle jument que vous m'offrez la. Il est vrai que je n'ai pas eu l'occasion de monter depuis mon arriv\xe9 en ville. Je vous remercie de se pr\xe9sent Mr Dyers, je ferais en sorte qu'elle ne finisse pas dans les estomacs de mes chatons"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"william-dyers-4",children:"William Dyers"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"25-02-2023 22:00:29"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"M\xeame si je n'obtiens pas le m\xeame effet de merveilleux que j'ai pu obtenir en offrant une arme au future mari, la jeune fille ne semble pas pour autant d\xe9\xe7ue. L'un de ses lions a bien senti une proie potentielle mais elle le retient, ce que j'imagine \xeatre une bonne chose. Sans se montrer tr\xe8s d\xe9monstrative, elle me remercie, m'assurant ne pas vouloir qu'elle termine en plat pour l'un de ses compagnons. Compte tenu du prix exorbitant que m'a co\xfbt\xe9 cette b\xeate, j'esp\xe8re bien. Ou alors, qu'elle ait au moins bon go\xfbt. Je me contente de rester sur place avec un l\xe9ger sourire victorieux."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Ravi qu'il vous plaise. Vous risquez de faire des jaloux, nous n'avons aucun cheval ni aucune \xe9curie \xe0 Langdorf. Enfin, jusqu'\xe0 aujourd'hui."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Je garde mes mains dans le dos en maintenant ma pose, observant l'animal et les r\xe9actions des gens aux alentours, \xe9tonn\xe9 de voir un cheval en ville."})})]})}function d(e={}){const{wrapper:n}={...(0,a.R)(),...e.components};return n?(0,r.jsx)(n,{...e,children:(0,r.jsx)(c,{...e})}):c(e)}},28453:(e,n,i)=>{i.d(n,{R:()=>l,x:()=>t});var r=i(96540);const a={},s=r.createContext(a);function l(e){const n=r.useContext(s);return r.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function t(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(a):e.components||a:l(e.components),r.createElement(s.Provider,{value:n},e.children)}}}]);