"use strict";(self.webpackChunkarchive_dernier_verre=self.webpackChunkarchive_dernier_verre||[]).push([[4542],{78029:(e,s,n)=>{n.r(s),n.d(s,{assets:()=>u,contentTitle:()=>l,default:()=>c,frontMatter:()=>a,metadata:()=>t,toc:()=>o});var r=n(74848),i=n(28453);const a={title:"2023-01-18 - Bureau du g\xe9rant (Oria, William)",toc_min_heading_level:4,toc_max_heading_level:4,tags:["tag_OriaLevenski","tag_WilliamDyers"]},l="Bureau du g\xe9rant (Oria, William)",t={id:"Archives/Q1_2023/2023-01-18_Oria_William",title:"2023-01-18 - Bureau du g\xe9rant (Oria, William)",description:"Bureau du g\xe9rant - 18/01/2023",source:"@site/docs/Archives/Q1_2023/2023-01-18_Oria_William.md",sourceDirName:"Archives/Q1_2023",slug:"/Archives/Q1_2023/2023-01-18_Oria_William",permalink:"/ArchiveLeDernierVerre/docs/Archives/Q1_2023/2023-01-18_Oria_William",draft:!1,unlisted:!1,tags:[{inline:!1,label:"Oria Levenski",permalink:"/ArchiveLeDernierVerre/docs/tags/OriaLevenski",description:"Archives o\xf9 Oria Levenski intervient"},{inline:!1,label:"William Dyers",permalink:"/ArchiveLeDernierVerre/docs/tags/WilliamDyers",description:"Archives o\xf9 William Dyers intervient"}],version:"current",frontMatter:{title:"2023-01-18 - Bureau du g\xe9rant (Oria, William)",toc_min_heading_level:4,toc_max_heading_level:4,tags:["tag_OriaLevenski","tag_WilliamDyers"]}},u={},o=[{value:"Bureau du g\xe9rant - 18/01/2023",id:"bureau-du-g\xe9rant---18012023",level:3},{value:"William Dyers",id:"william-dyers",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves",level:4},{value:"William Dyers",id:"william-dyers-1",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-1",level:4},{value:"William Dyers",id:"william-dyers-2",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-2",level:4},{value:"William Dyers",id:"william-dyers-3",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-3",level:4},{value:"William Dyers",id:"william-dyers-4",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-4",level:4},{value:"William Dyers",id:"william-dyers-5",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-5",level:4},{value:"William Dyers",id:"william-dyers-6",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-6",level:4},{value:"William Dyers",id:"william-dyers-7",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-7",level:4},{value:"William Dyers",id:"william-dyers-8",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-8",level:4},{value:"William Dyers",id:"william-dyers-9",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-9",level:4},{value:"William Dyers",id:"william-dyers-10",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-10",level:4},{value:"William Dyers",id:"william-dyers-11",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-11",level:4},{value:"William Dyers",id:"william-dyers-12",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-12",level:4},{value:"William Dyers",id:"william-dyers-13",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-13",level:4},{value:"William Dyers",id:"william-dyers-14",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-14",level:4},{value:"William Dyers",id:"william-dyers-15",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-15",level:4},{value:"William Dyers",id:"william-dyers-16",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-16",level:4},{value:"William Dyers",id:"william-dyers-17",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-17",level:4},{value:"William Dyers",id:"william-dyers-18",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-18",level:4},{value:"William Dyers",id:"william-dyers-19",level:4},{value:"Oria Levenski ~ Responsable des esclaves",id:"oria-levenski--responsable-des-esclaves-19",level:4},{value:"William Dyers",id:"william-dyers-20",level:4}];function d(e){const s={admonition:"admonition",blockquote:"blockquote",em:"em",h1:"h1",h3:"h3",h4:"h4",hr:"hr",li:"li",p:"p",strong:"strong",ul:"ul",...(0,i.R)(),...e.components};return(0,r.jsxs)(r.Fragment,{children:[(0,r.jsx)(s.h1,{id:"bureau-du-g\xe9rant-oria-william",children:"Bureau du g\xe9rant (Oria, William)"}),"\n",(0,r.jsx)(s.admonition,{title:"Le Dernier Verre - 3\xe8me \xe9tage",type:"info",children:(0,r.jsx)(s.h3,{id:"bureau-du-g\xe9rant---18012023",children:"Bureau du g\xe9rant - 18/01/2023"})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"18-01-2023 23:32:47"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"J'ai beau avoir eut l'impression qu'Oria me suivait tout du long de ma visite, je remarque en me retournant une fois dans l'ascenseur qu'elle a r\xe9ussit a r\xe9cup\xe9rer ses affaires qui inclut, entre autre, un casque de moto. Peu commun comme moyen de locomotion ici et pourtant pas idiot. Je d\xe9taille rapidement du regard ses effets pendant qu'elle m'explique avoir simplement utilis\xe9 le jour le plus calme en terme de clients."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Parfait alors. Il faudra conserver le march\xe9 en cet \xe9tat maintenant, m\xeame si j'imagine que c'\xe9tait d\xe9j\xe0 dans tes objectifs personnels."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"J'ai un petit regard complice \xe0 la lapine avant de me reporter \xe0 la porte en face de nous. L'ascenseur ralentit jusqu'\xe0 s'arr\xeater, les portes s'ouvrent et j'emboite le pas dans le couloir, me dirigeant directement vers mon bureau soit la premi\xe8re porte du couloir. J'ouvre la porte en laissant Oria entrer la premi\xe8re et la suis en retirant ma veste."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Bienvenue dans mon bureau. Tiens, prends un fauteuil, tu peux d\xe9poser tes affaires \xe0 l'entr\xe9e si tu veux. Je te sers quoi ? Whisky ?"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"18-01-2023 23:57:47"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Oh oui, c'\xe9tait bien son intention de venir garder le march\xe9 dans cet \xe9tat, et elle avait d'ailleurs beaucoup d'id\xe9es pour le march\xe9. Dans un sens, elle \xe9tait heureuse de ne pas avoir rencontr\xe9 l'employeur qui l'avait fait venir dans cette ville. Cette nouvelle profession \xe9tait des plus enrichissante pour elle et pour le moment elle s'y plaisait beaucoup. Continuant de le suivre \xe0 le sortie de l'ascenseur, la lapine le remercia d'un sourire quand il se fit gentleman en la laissant passer la premi\xe8re. D\xe9posant alors sa veste et son casque au porte manteau avant d'observer le lieu. Puis son regard fut frapp\xe9 par la beaut\xe9 de son bureau et surtout par l'immense biblioth\xe8que qu'il poss\xe9dait. Dans un lieu comme celui-ci, la responsable pourrait passer des heures \xe0 lire un livre assise face \xe0 la chemin\xe9e, celle ci donnant une ambiance tout \xe0 fait propice pour ce genre d'activit\xe9. William viendra \xe0 la ramener \xe0 la r\xe9alit\xe9 quand il lui proposa de s'installer, ce qu'elle ira faire, profitant de l'assise confortable du fauteuil."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Ecossais j'esp\xe8re, je n'en ai pas bu depuis longtemps"}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Continuant d'observer l'endroit avec ses yeux vairons, elle repose ensuite son regard sur lui avec un petit sourire."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je pense que je vais devenir jalouse de votre bureau par contre Mr Dyers"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-1",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"19-01-2023 17:12:25"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"A peine rentr\xe9, je vois le regard d'Oria se promener dans la pi\xe8ce avec ces petites \xe9toiles, fascin\xe9e par l'endroit comme un enfant peut l'\xeatre \xe0 No\xebl. Elle me suis sur la boisson en acceptant le whisky avec un trait d'humour flashback, se rem\xe9morant surement la discussion que nous avions eut \xe0 son arriv\xe9e."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Bien entendu \xe9cossais, c'est un 30 ans d'\xe2ge, une pure merveille."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Pr\xe8s du mur, pas tr\xe8s loin de mon bureau, se trouve une petite table faisant office de bar et, sur celle-ci, une bouteille en cristal qui contient le dit liquide ainsi que quelques verres. J'en sers deux, faisant un petit rire en entendant la remarque de la lapine concernant la pi\xe8ce."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je con\xe7ois et je le prend comme un compliment. C'est une pi\xe8ce qui \xe9tait pens\xe9e pour recevoir les puissants de ce monde - ou du moins de cette ville - et, \xe0 d\xe9faut, c'est un lieu agr\xe9able o\xf9 travailler. Si cela peut te donner une raison de venir me voir plus souvent, moi je suis d'accord ! Tu prend ton whisky avec de la glace ?"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-1",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"19-01-2023 18:07:51"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Heureuse qu'il se souvienne de leur \xe9change par rapport \xe0 l'alcool, elle replace une m\xe8che de cheveux en souriant timidement tout en observant du coin de l'oeil les ouvrages pr\xe9sent. Une de ses oreilles viendra a se tourner en entenant le blond remplir leur verre. De la elle pouvait d\xe9j\xe0 sentit la douce odeur du bravache qu'elle allait gouter."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Ne me dite pas cela voyons, je risque de passer mon temps ici a chacune de mes pauses pour lire un ouvrage au coin de la chemin\xe9 Mr Dyers. Et je le prendrais sans glace pour ma part"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-2",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"19-01-2023 22:02:10"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"J'attends la r\xe9ponse d'Oria et, vu qu'elle le prend comme moi, sec, je viens lui apporter son verre puis m'assoit dans le fauteuil en face. Sa remarque me fait sourire et je fais une petite moue en haussant les \xe9paules"})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Ce n'est pas dit que \xe7a me d\xe9range. Je ne suis jamais contre un peu de compagnie tu sais ? En particulier lorsqu'il s'agit d'une femme."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je m'installe confortablement dans le fauteuil avec un petit mouvement du fessier puis l\xe8ve le verre \xe0 la lapine. Je viens simplement tremper mes l\xe8vres dans le liquide, juste assez pour profiter de l'ar\xf4me de l'alcool."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Comment se passe ces premiers jours alors ? Contente de ton poste ?"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-2",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"19-01-2023 23:31:10"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Ses yeux vairons continuait d'observer William avant de le remercier d'un regard quand il lui apporta son verre. De l\xe9g\xe8re rougeur viendront \xe0 appara\xeetre quand il lui annon\xe7a que sa compagnie ne viendra pas \xe0 la d\xe9ranger. Croisant les jambes sur le c\xf4t\xe9s, comme si elle portait une jupe, elle l\xe8vera \xe0 son tour son verre avant de humer le liquide en fermant les yeux. Diff\xe9rent du whiskey qu'elle lui avait fait go\xfbter la derni\xe8re fois, celui ci aurait \xe9t\xe9 un vrai sacril\xe8ge de pas le servir dans un verre. Buvant \xe0 son tour une petite gorg\xe9, elle laissa le liquide r\xe9chauffer son corps avant de rouvrir les yeux pour observer son patron."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Tout ce passe pour le mieux, les vendeurs m'on aid\xe9 \xe0 prendre rapidement mes marques. Et pour tout vous avouez je suis bien contente de ne pas avoir rencontrer l'employeur qui m'avait fait venir ici, ce poste est tr\xe8s enrichissant je trouve"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-3",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"21-01-2023 12:02:01"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Maintenant tout deux assis, j'ai l'impression que la lapine se d\xe9tend enfin. Encore que ce n'est surement qu'une impression par le fait que je ne fois pas sa petite queue remuer presque continuellement. Elle boit une petite gorg\xe9e du whisky avant de me r\xe9pondre, se disant heureuse de son poste et m\xeame heureuse de ne pas avoir eut le job qui l'avait fait venir \xe0 Langdorf."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je ne sais pas quel \xe9tait cet emploi mais je confirme, il a bien fait de ne pas donner suite. Ce n'est pas moi qui m'en plaindrais en tout cas et je suis content que tu te plaise ici."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je reprends une petite gorg\xe9e de whisky et d\xe9tourne mon regard sur la biblioth\xe8que. La lapine m'a parler de livre et je me demande bien lesquels ont pu attirer son attention. Je passe aussi mon regard sur la chemin\xe9e, ferm\xe9e et \xe9teinte et fait un petit soupire"})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"La prochaine fois je ferais un feu. Enfin, si tu ne viens pas avec une peau de b\xeate pour t'y allonger. L\xe0, je ne r\xe9pondrais plus de rien. Mais un peu de chaleur naturel dans cette p\xe9riode, ce serait id\xe9al."}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-3",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"21-01-2023 15:51:00"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Gardant son verre dans la main, elle continuait de regarder son employeur dans les yeux avec un petit sourire timide. Jusqu'\xe0 ce que de petite rougeur apparaisse sur ses joues quand il fit l'allusion \xe0 une peau de b\xeate. Rigolant m\xeame l\xe9g\xe8rement, elle pensait \xe0 son petit chalet qu'elle avait fait construire. Que ce passerait-il s'il venait chez elle avec son ambiance assez rustique et chaleureuse ?"})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je ne connaissait pas son nom mais mon ancien patron m'avait dit que je le reconnaitrais facilement avec sa canne et son immense balafre sur le visage. Peut-\xeatre le connaissez vous ?"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-4",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"21-01-2023 21:04:35"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"La lapine me parle bri\xe8vement de son employeur premier, celui qui l'a motiv\xe9e \xe0 venir en ville. Les caract\xe9ristiques physiques qu'elle me donne sont significatives et si je pense tout d'abord \xe0 Nathan qui fait beaucoup parler de lui pour sa canne ces derniers jours, la balafre l'exclue rapidement et me met sur la bonne piste."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Oh, M. Legrand j'imagine. Une personne fiable, bien qu'un peu lubrique si je me fis aux bruits de couloirs. J'imagine qu'il a d'autres priorit\xe9 maintenant qu'il travaille pour les siciliens que d'avoir une comptable... Secr\xe9taire ? Enfin quelle que soit la raison pour laquelle il pensait t'employer."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je regarde Oria avec un petit sourire, bien content de l'avoir dans mon \xe9quipe. Pour la \xe9ni\xe8me fois, je la d\xe9taille du regard avant de reprendre."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je ne te pensais pas motarde d'ailleurs. C'est un excellent choix pour se d\xe9placer en ville mais j'ai toujours du mal \xe0 imaginer une femme sur un gros engin. Sans doute mon c\xf4t\xe9 vieux jeu."}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-4",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"21-01-2023 21:11:38"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"A l'annonce de ce fameux \"Legrand\", son regard se vis quelque peu froid. Clairement elle \xe9tait tr\xe8s contente de ne pas l'avoir rencontrer car jamais elle n'aurait jamais travailler pour lui. Pas avec le pass\xe9 qu'elle avait eu avec lui. Reprenant son calme, elle but une gorg\xe9 de son alcool. Ce n'\xe9tait clairement pas le moment de parler de cet abrutit de Mark. Rigolant l\xe9g\xe8rement quand il viendra \xe0 faire la remarque de son moyen de transport, elle l'observa de ses yeux vairons."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Apr\xe8s je ne conduis pas de sportive, met une Harley. J'ai fait la route de la France jusqu'ici avec. Et vous, comment vous d\xe9placer vous g\xe9n\xe9ralement en ville Mr Dyers ?"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-5",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"22-01-2023 00:39:03"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Le changement d'expression lorsque je prononce le nom de Mark est si flagrant que je peux difficilement le rater. Je ne sais pas quel passif il y a entre les deux mais c'est un sujet qu'il faudra aborder avec des pincettes s'il revient sur la table. Heureusement, le froid que j'ai jet\xe9 se dissipe bien vite lorsque je la questionne sur sa moto. Oria retrouve le sourire et me rend mon regard tout en me r\xe9pondant."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Une Harley ?! Rien que \xe7a. La route n'a pas d\xfb \xeatre \xe9vidente. Pour ma part, j'ai un chauffeur et, compte tenu du poste que j'occupe, je me d\xe9place en v\xe9hicule blind\xe9. On n'est jamais trop prudent."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Avec un petit sourire, je soutiens son regard puis je prend une nouvelle petite gorg\xe9e de whisky avant de reprendre."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Vous venez de France, c'est vrai. Ca ne vous manque pas ? Enfin j'ai entendu dire que c'\xe9tait un pays tr\xe8s port\xe9 sur la cuisine et je doute que vous retrouviez votre bonheur dans notre ville."}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-5",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"22-01-2023 23:33:47"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Etrangement cela ne l'\xe9tonna que tr\xe8s peu de savoir que William avait non seulement un chauffeur mais aussi un v\xe9hicule blind\xe9. Avec une ville comme Langdorf, c'\xe9tait la moindre des choses \xe0 avoir, surtout avec le poste qu'il occupait. Buvant de temps \xe0 autre son verre, elle laissait le liquide r\xe9chauffer son corps. M\xeame ses petites joues prenait quelque couleurs."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Avant de m'installer en France je vivais en Angleterre, j'aime le changement, d\xe9couvrir de nouvelle chose. Si vous le souhaitez un jour je vous emm\xe8nerais faire un tour en Harley, je ne propose pas \xe7a \xe0 tout le monde alors vous pouvez \xeatre chanceux Mr Dyers"}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Elle continuait de l'observer, son pon-pon fr\xe9tillant l\xe9g\xe8rement sans qu'il ne puisse le voir."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Et par piti\xe9 tutoyez moi je vous en pris"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-6",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"23-01-2023 19:07:40"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Oria m'explique \xeatre bien originaire de France mais m'avoue avoir v\xe9cu en Angleterre. Encore un pays que je connais mal, hormis pour le peu dont Nathan a pu me parler. Elle m'invite ensuite \xe0 faire un tour de moto, proposition \xe9tonnante mais qui m\xe9rite r\xe9flexion. Enfin, qui aurait m\xe9rit\xe9 car Oria me reprend \xe0 nouveau sur mon vouvoiement, me faisant rire de g\xe8ne."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"ahahah, j'essaie pourtant d'y faire attention mais les habitudes ont la vie dure. Ca va venir, \xe7a va venir, ne t'en fait pas. Et je suis d\xe9j\xe0 chanceux d'avoir une responsable de si bonne qualit\xe9. A la fois bonne dans son travail et, reconnaissons le, agr\xe9able \xe0 regarder. Ce n'est pas exclu que j'accepte cette invitation un de ces jours, juste pour voir."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je remarque que nos verres sont pratiquement vides. Un bon whisky a beau se savourer, comme tous les whisky, il finit par ne plus y en avoir dans le verre. Je lui montre mon verre et le d\xe9signe de mon autre main."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Un autre ? Peut \xeatre pas tr\xe8s raisonnable mais \xe7a fait bien longtemps que ce n'est plus un crit\xe8re que je consid\xe8re."}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-6",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"24-01-2023 18:27:35"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Ses oreilles se mirent l\xe9g\xe8rement \xe0 fr\xe9tiller en l'entendant rire, un son agr\xe9able pour la lapine. Comprenant que c'\xe9tait encore un peu compliqu\xe9 pour son patron de la tutoyer \xe9tant donn\xe9 qu'il se connaissait a peine. A nouveau elle viendra \xe0 rougir l\xe9g\xe8rement en entendant le compliment de William, ses oreilles se penchant m\xeame quelque peu sur le c\xf4t\xe9. Il l'aurait connu quelques ann\xe9es plut\xf4t il n'aurait pas eu le m\xeame discours et n'aurait \xe7a se trouve m\xeame pas poser un regard sur elle. Oria viendra \xe0 regarder sa montre \xe0 son poignet quand il lui proposa un autre verre, le siens \xe9tant vide. Naturellement elle viendra lui tendre avec un petit sourire."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Et bien j'ai fini ma journ\xe9e de travail et comme me le disait souvent mon ancien patron, il faut boire avec mod\xe9ration. Vous connaissez mod\xe9ration vous ? Car j'aimerais beaucoup le rencontrer un jour"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-7",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"25-01-2023 23:41:47"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je remarque la r\xe9action de la lapine \xe0 mon compliment. Je crois que c'est la premi\xe8re fois que j'avoue appr\xe9cier son physique. G\xe9n\xe9ralement, je n'aime pas partager ce genre d'impression et de relation avec mes employ\xe9s mais l'alcool d\xe9lie les langues, en particulier quand il a v\xe9cu plusieurs dizaines d'ann\xe9es en f\xfbt de ch\xeane. Apr\xe8s un regard pour sa montre, Oria fait un trait d'humour qui me fait sourire sur le moment avant que je reprenne, aussi s\xe9rieux que possible."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je crois que c'est le petit nom que je donne \xe0 mon sexe. Popol \xe7a fait tellement d\xe9mod\xe9 et comme \xe7a, il trempe dans beaucoup d'histoire."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je me l\xe8ve en pouffant de rire. Mes b\xeatises me feront toujours rire je crois. Je vais vers la petite table prendre la bouteille de whisky et viens en servir une seconde dose \xe0 la jeune fille avant de me servir aussi et je me rassois dans mon fauteuil, posant la bouteille sur un meuble non loin."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Et... Quitte a glisser sur le sujet, il n'y a pas de M. Levenski mais il n'y en a jamais eut ? Ou peut \xeatre plut\xf4t une madame ? Je n'ai aucun soucis avec si c'est le cas, saches le"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-7",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"26-01-2023 00:11:24"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Ses joues toujours l\xe9g\xe8rement rouge a son compliment, la lapine continuait de le regarder de ses yeux vairons. L'alcool n'aidait pas non plus \xe0 calmer ses rougeurs, elle tenait l'alcool et la rendait m\xeame un peu moins timide. Elle viendra m\xeame \xe0 rigoler l\xe9g\xe8rement en entendant le petit surnom qui donnait \xe0 son attribut masculin. Tr\xe8s clich\xe9 d'ailleurs ne nous mentons pas. Prenant le verre qu'il venait \xe0 lui tendre, elle viendra \xe0 croiser les jambes sans le quitter des yeux."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"A part mes parents aucun Mr ou Mme Levenski. Ca fait d'ailleurs un moment que je n'ai pas partager ma couche avec quelqu'un"}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"L'alcool d\xe9liait les langues et un peu trop celle de Oria, qui viendra a rougir suite \xe0 ce qu'elle venait de dire. G\xean\xe9, ses oreilles viendrons \xe0 se plaquer de chaque c\xf4t\xe9 de sa t\xeate. Il lui fallu quelque instant pour se reprendre et aussi une bonne gorg\xe9 d'alcool."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Et vous ? Vous devez surement avoir une Mme ou un Mr Dyers \xe0 vos c\xf4t\xe9 ? Un homme de votre stature ne doit pas souvent \xeatre seul je pense"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-8",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"26-01-2023 01:35:18"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Pour le moins perspicace, la lapine a une r\xe9ponse tr\xe8s habile qui aurait pu compl\xe9tement d\xe9tourner la conversation en usant de ses parents. Mais elle me r\xe9pond malgr\xe9 tout par la n\xe9gative, avouant m\xeame un peu plus que ce que j'avais demand\xe9. En tout cas, vu sa r\xe9action, \xe7a semble lui avoir \xe9chapp\xe9 et je trouve cela assez mignon. Nous prenons une gorg\xe9e simultan\xe9ment avant qu'elle ne me retourne la question."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Ah. Ma vie sentimentale est malheureusement compliqu\xe9e. Je me suis bien mari\xe9 \xe0 une \xe9poque mais ma femme a disparu depuis longtemps. Je pense que je ne la reverrais pas d\xe9sormais. C'est la contrepartie de mon m\xe9tier : je vis dangereusement et mes proches encore plus. J'aimerais pourtant avoir une descendance mais je n'ai pas encore trouv\xe9 de bon parti pour cela."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je fais un soupir, \xe0 la fois d\xe9\xe7u et triste en r\xe9alisant que je ne reverrais surement jamais Hana\xe9 et que la vie n'est pas tendre avec moi concernant l'amour. Mais je ne me laisse pas abattre bien longtemps et reprends en redressant le regard avec un sourire un peu forc\xe9"})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Par contre j'ai la chance d'avoir une esclave pour ce qui est du sexe depuis quelques semaines et je dois bien reconnaitre qu'avoir des rapports sexuels r\xe9guliers a grandement am\xe9lior\xe9 mon moral. J'ai m\xeame l'impression que je vais mieux : meilleur sant\xe9, moins de fatigue, moins de douleurs... Sans vouloir paraitre indiscret, tu n'as personne dans ta couche par choix ou par manque de pr\xe9tendant convenable ?"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-8",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"26-01-2023 02:08:54"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Il est vrai que le poste de William \xe9tait pour le moins risquer et que garder une \xe9pouse \xe0 ses c\xf4t\xe9 devait \xeatre pour le moins compliqu\xe9. Devoir se prot\xe9ger \xe9tait une chose mais prot\xe9ger ses proches devait \xeatre dangereux car cela pourrait \xeatre une faiblesse \xe0 exploiter pour le blond. Voyant alors sa mine quelque peu triste, elle posa une main sur son genoux avec un sourire timide qui se voulait rassurant. Mais voyant son contact physique quelque peu trop proche, elle retira sa main en rougissant tout en continuant de l'\xe9couter. C'est alors qu'il viendra parler de ses contacts avec son esclaves. C'est vrai que les contacts physique \xe9taient le meilleurs des rem\xe8des."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Et bien .. Voyez vous .."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Elle fut l\xe9g\xe8rement g\xean\xe9 et attrapa sa m\xe8che de cheveux un peu plus longue pour l'entortiller autour de son dos."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je n'ai pas toujours eu cette apparence, il y a quelques ann\xe9es vous n'aurez surement pas pos\xe9 un regard sur moi. Ca \xe0 laisser des s\xe9quelles sur mon corps que j'ai du mal \xe0 assumer une fois d\xe9nuder"}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"La lapine continuait de boire son verre tout en baissant le regard, ses oreilles s'agitants quelques peu en avouant cela. Elle n'avait plus de timidit\xe9 en elle quand elle buvait et avec un alcool aussi fort et bon ..."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-9",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"26-01-2023 18:23:59"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Oria est la premi\xe8re a cr\xe9er un contact physique entre nous apr\xe8s m'avoie entendu parler de ma femme... Ou plut\xf4t ex-femme d\xe9sormais. A sa mani\xe8re, elle trouve \xe0 me consoler en me ramenant sur terre, un peu comme on tend la main \xe0 quelqu'un qui se noie pour le sortir hors de l'eau. Main qu'elle retire d'ailleurs bien vite sans en dire plus \xe0 ce sujet. Peut \xeatre un peu trop proche pour un premier contact"})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Lui demander les raisons de son c\xe9libat n'arrange rien \xe0 sa g\xe8ne d'ailleurs. Avec une grande timidit\xe9, sans oser me regarder, elle me parle de son histoire sans entrer dans les d\xe9tails. Elle parle de s\xe9quelles laiss\xe9es sur son corps qu'elle n'assume pas vraiment et cela pique ma curiosit\xe9. Une hybridation post-natale ? Peut \xeatre m\xeame tardive. Ou bien une chirurgie esth\xe9tique qui a mal tourn\xe9 ? Peut \xeatre simplement des cicatrices disgracieuses ? Mais une petite part de moi ne peut s'emp\xeacher de se dire qu'elle pourrait \xeatre bien \xeatre n\xe9 d'un autre sexe."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je vois. Tu as besoin d'une confiance encore plus grande envers ton partenaire pour te montrer alors. \xc9coute, je ne sais pas ce dont il s'agit mais ce qu'on \xe9tait avant, \xe7a ne compte pas. C'est ce qu'on est aujourd'hui qui compte et moi je vois un tr\xe8s jolie jeune femme qui ne manque pas de charme en plus d'\xeatre professionnelle."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je lui fais un petit sourire sinc\xe8re et, de la m\xeame mani\xe8re qu'elle a cherch\xe9 \xe0 me rassurer, je viens poser une main sur sa jambe au niveau de son genou. A l'exception pr\xe8s que je ne la retire pas ni ne la bouge. C'est peut \xeatre ce mouvement qui nous rapprocheras assez pour que je la tutoie sans m\xeame y penser."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Regarde moi par exemple, j'ai tout un paquet de cicatrices sous ce costume et je le vis tr\xe8s bien. Ca fait des histoires \xe0 raconter. Certains aiment, d'autres non, c'est ainsi. En v\xe9rit\xe9, je pense m\xeame que dans d'autres circonstances, j'aurais cherch\xe9 \xe0 avoir une autre relation avec toi. Peut \xeatre m\xeame \xe0 me marier avec toi, qui sait ?"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-9",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 04:19:36"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Levant doucement ses yeux vairons en regardant son patron, elle eu un doux sourire en entendant qu'il essayait de la r\xe9conforter. Cela lui faisait chaud au c\u0153ur. Le pass\xe9 \xe9tait pass\xe9 et elle devait toujours avoir un regard porter sur l'avenir. Un avenir ici, dans cette ville, du moins elle l'esp\xe9rait. Son regard viendra \xe0 se poser sur la main pos\xe9 sur son genoux. Sauf que a contrario il la laissa sur son genoux, comme pour appuyer ses propos et la rassurer."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Mes cicatrices sont disgracieuses, les v\xf4tres doivent raconter une histoire, voir m\xeame vous embellir"}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"La lapine \xe9carquilla les yeux en \xe9coutant sa derni\xe8re question. Relevant les yeux vers lui, elle le regarda quelque instant avant de doucement rigoler. Lui alors, elle ne savait pas si c'\xe9tait s\xe9rieux ou pas mais dans ton les cas cela viendra \xe0 la d\xe9tendre."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Cela serait un grand \xe9v\xe8nement si le chef de cette ville viendrait \xe0 se marier je pense, j'esp\xe8re au moins que je serais invit\xe9 si cela viendrais \xe0 arriver"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-10",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 08:26:28"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Difficile de dire si mes mots arrivent \xe0 toucher la lapine. Elle a bien un sourire et ne semble pas particuli\xe8rement g\xean\xe9e par la pr\xe9sence de ma main sur sa cuisse. De l\xe0 \xe0 dire qu'elle est convaincue, peut \xeatre pas. Pour elle, ses cicatrices sont laides l\xe0 o\xf9 les miennes sont l'occasion d'une anecdote et joue sur mon charme."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Toutefois, la suggestion d'un mariage fait son effet et je la vois rougir d'un coup. Sans doute imagine t elle la situation avant de se mettre \xe0 rire et de d\xe9tendre l'atmosph\xe8re en d\xe9tournant la conversation sur l'\xe9v\xe9nement en lui m\xeame et non la possibilit\xe9 qu'elle soit ma femme."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Le dernier mariage a \xe9t\xe9 fait en petit comit\xe9, je n'ai jamais \xe9t\xe9 tr\xe8s fan des grands \xe9v\xe9nements. Encore qu'avec la r\xe9ception du nouvel an, je me dit qu'il faut peut \xeatre \xe9voluer et f\xeater ce prochain mariage dignement. Nous verrons bien."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je retire ma main de sa cuisse en me r\xe9installant dans mon si\xe8ge, le temps d'une petite gorg\xe9e alcoolis\xe9e. Ma curiosit\xe9 reste piqu\xe9e concernant ses fameuses cicatrices et, l'alcool aidant surement, je viens poser mon verre pour me redresser dans mon fauteuil pour d\xe9faire ma cravate, la retirer, puis ouvrir ma chemise pour r\xe9v\xe9ler sans honte ni g\xe8ne mes cicatrices."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Allez, montre moi ces cicatrices. Je n'arrive pas \xe0 croire que \xe7a puisse \xeatre si terrible. Puisqu'il faut se rapprocher pour que je te tutoie naturellement, montrons nous respectivement ce que la vie nous a laiss\xe9 comme souvenir."}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-10",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 09:08:02"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Oria viendra \xe0 l'\xe9couter parler d'une r\xe9ception qu'il avait surement organis\xe9 pour la nouvelle ann\xe9e. Elle aurait bien aim\xe9e y participer, la jeune femme n'avait pas assister \xe0 des r\xe9ceptions ou des galas depuis qu'elle avait quitt\xe9 les terres anglaises. Avec un air de nostalgie, elle viendra \xe0 boire une gorg\xe9 d'alcool tout en l'observant retirer sa main. Elle faillit d'ailleurs s'\xe9touffer avec quand elle le verra ouvrir sa chemise devant elle. Rougissant de plus belle, il lui fallu une gorg\xe9 de plus avant d'avoir un sourire l\xe9g\xe8rement joueur."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Quand je disais que vos cicatrices vous rendais plus beau j'avais raison, \xe7a vous donne un petit air de mafieux"}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"La lapine observait discr\xe8tement son torse m\xeame si elle se focalisait principalement sur son regard. Rigolant ensuite l\xe9g\xe8rement, elle posera \xe0 son tour son verre. Apr\xe8s un petit moment de r\xe9flexion, elle l\xe8vera son d\xe9bardeur rouge jusqu'en dessous de son soutien gorge. Un ventre l\xe9g\xe8rement dessin\xe9 mais couvert de vergetures. D\xe9tournant le regard, elle restait tout de m\xeame g\xean\xe9."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Et j'en ai aussi sur les cuisses et au niveau de la poitrine .. Quand je vous disais que vous n'auriez surement pas porter un regard sur moi c'est que peu de monde s'int\xe9resse aux ob\xe8ses Mr Dyers"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-11",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 09:52:54"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Oria ne s'attendait surement pas \xe0 me voir ainsi, avalant de travers son whisky en me voyant ouvrir ma chemise et toussant aussit\xf4t. Le rouge lui monte aussit\xf4t aux joues et elle regarde mon torse par petits regards brefs, comme si elle cherchait \xe0 le faire en cachette ou de mani\xe8re d\xe9tourn\xe9e. Je ne sais pas si c'est mon statut de patron ou l'absence d'homme dans sa vie que la fait r\xe9agir ainsi mais de mon c\xf4t\xe9, je n'ai aucune g\xeane particuli\xe8re \xe0 lui montrer ma peau meurtrie."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Elle h\xe9site \xe0 suivre ma demande et honn\xeatement, je m'attends \xe0 tout. Une cicatrice profonde qui lui parcours le corps, un muscle manquant, une chirurgie esth\xe9tique rat\xe9e et m\xeame qu'elle baisse son pantalon. Mais non, j'ai tort sur toute la ligne. Elle remonte son haut et me montre son ventre stri\xe9 de ligne rouge, bleue ou pourpre. Je la regarde sans trop comprendre mais quand elle m'explique avoir \xe9t\xe9 ob\xe8se, tout s'explique. De simples vergetures, de la peau d\xe9chir\xe9e de l'int\xe9rieur."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je suis tellement \xe9tonn\xe9 de la r\xe9ponse qu'il me faut un certain temps pour assimiler l'information. Je m'attendais tellement \xe0 un changement de sexe que son probl\xe8me me semble presque anodin sur le moment mais je contextualise peu \xe0 peu et apr\xe8s un long instant o\xf9 j'ai surement remu\xe9 les l\xe8vres en cherchant les mots appropri\xe9s, je reprend enfin."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je comprends mieux, oui. Ca ne doit pas \xeatre facile de passer d'un physique sur lequel personne ne se retourne \xe0 celui que tu as aujourd'hui et qui doit t'attirer tous les regards et les envies. Vu mon \xe9tablissement et les poup\xe9es qui y travaille, je suis bien mal plac\xe9 pour dire le contraire."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"La surprise pass\xe9, je me redresse un peu pour observer les vergetures qu'elle me montre. Si elles peuvent paraitre laide pour certains, j'y vois un certain charme. Comme une forme de tatouage chaotique ou un mouchetage d'animal. Je remonte mon regard vers son visage, sentant bien sa g\xe8ne d'avoir \xe0 me montrer ce passif qui doit lui \xeatre compliqu\xe9."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je con\xe7ois que \xe7a ne colle pas aux exigences de beaut\xe9 mais je ne trouve pas \xe7a d\xe9plaisant. Je trouve m\xeame que \xe7a te donne un charme, comme une route toute trac\xe9 o\xf9 passer sa langue ou... Autre chose. Tu permet que j'y touche ? Avec les doigts j'entends, juste pour voir la sensation que cela peut procurer."}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-11",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 10:32:41"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Les oreilles plaqu\xe9s de chaque c\xf4t\xe9s de la t\xeate, le d\xe9bardeur relever de sa main droite, la gauche \xe9tait referm\xe9 sur sa propre cuisse. Ce n'\xe9tait pas souvent qu'elle montrait ses cicatrices sous la lumi\xe8re. Elle avait du mal \xe0 affronter le regard du blond mais apr\xe8s un petit moment, ses yeux vairons se l\xe8verons sur lui. A quoi pensait-il ? La trouvait-elle hideuse ? Non clairement pas. Il semblait m\xeame surpris d'une telle r\xe9v\xe9lation."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Apr\xe8s tout, elle ne croisait que de belle et jolie jeune femme dans l'\xe9tablissement. Et avec les esclaves qu'elle vendait, Oria avait de quoi jalouser leurs peaux des fois parfaites. Une oreille viendra \xe0 se redresser en le voyant se rapprocher l\xe9g\xe8rement d'elle pour observer les stries sur son ventre."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Un charme ? Vous devez bien \xeatre le premier \xe0 me dire \xe7a. Apr\xe8s je n'ai pas pour habitude de les montrer sans vous mentir .."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Attrapant son verre de sa main libre, il lui fallait bien une bonne gorg\xe9 d'alcool avec ce qu'il venait de lui demander. Timidement, la lapine viendra \xe0 attraper le poignet de William pour l'approcher de son ventre, laissant ses doigts toucher celui ci. Et heureusement que l'alcool commen\xe7ait \xe0 lui monter aux oreilles, sinon elle aurait d\xe9j\xe0 fuit chez elle pour se planquer sous une pile de bouquin."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-12",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 12:31:41"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Confirmant mes soup\xe7ons, la lapine m'avoue ne pas montrer facilement ces cicatrices. Pire encore, je serais le premier \xe0 la complimenter sur les marques de son ventre. Je suis assez \xe9tonn\xe9 mais la m\xe9chancet\xe9 des gens n'a malheureusement pas de limite. Oria reprend une gorg\xe9e de Whisky pour se donner du courage puis prend mon poignet pour l'approcher de son ventre, me laissant toucher ses vergetures."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je proc\xe8de doucement, passant tout ma paume et mes doigts sur son ventre, juste au dessus du nombril pour sentir les sillons dans la peau. C'est plus marqu\xe9 que je ne l'imaginais, comme si sa peau avait une granularit\xe9 irr\xe9guli\xe8re. Chaque trace rouge est l'occasion d'un petit creux. Je suis quelques unes d'entre elle avec le bout de mon index."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"C'est tr\xe8s int\xe9ressant. Bon j'ai l'impression de te faire quelque chose de tr\xe8s sexuel alors que pas du tout. Merci Oria."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Trop curieux, je ne r\xe9siste pas \xe0 l'envie de me rapprocher en me penchant sur le c\xf4t\xe9 du fauteuil pour voir de plus pr\xe8s ses marques. Je ram\xe8ne ma main au niveau de son rein, caressant sa peau du bout des doigts encore quelques instants avant de cesser ce qui doit \xeatre une torture pour elle"})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"J'esp\xe8re que je ne te fais pas mal. C'est bizarre comme parfois \xe7a semble presque \xeatre un simple tatouage et d'autre fois, c'est comme creus\xe9. Avec un petit haut en r\xe9sille, je suis s\xfbr que \xe7a peut \xeatre sexy. Tu n'a jamais song\xe9 \xe0 faire un tatouage autour de \xe7a ? A moins que \xe7a puisse disparaitre ou se modifier avec le temps ?"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-12",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 16:23:38"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"La lapina rentra l\xe9g\xe8rement le ventre en l\xe2chant doucement son poignet alors qu'elle le laissait toucher son ventre. Il \xe9tait extr\xeamement doux, d\xe9couvrant les craquelures avec d\xe9licatesse. A certains moment elle avait un frisson alors qu'elle venait \xe0 finir son verre cul sec pour se garder du courage. C'\xe9tait un sacril\xe8ge pour un alcool comme celui mais sur le moment elle en avait grandement besoin."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Timidement elle rel\xe8vera ses yeux vers lui quand il se stoppa pour laisser sa main dans le creux de son rein. Mais il avait raison dans un sens, elle aussi trouvait \xe7a un peu sexuel. Elle n'\xe9tait plus vierge certes mais chacune de ses relations c'\xe9taient pass\xe9 dans le noir le plus complet. Alors \xe0 la lumi\xe8re, cela donnait un tout autre aspect \xe0 cette sc\xe8ne pour elle. Ses joues \xe9taient rougis par la g\xeane mais aussi par l'alcool."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Cela ne fais pas mal non, c'est seulement plus sensible \xe0 certains endroit. Quand \xe0 les cacher en les tatouant et bien oui, \xe7a met d\xe9j\xe0 pass\xe9 par la t\xeate, mais je me suis toujours dis que mon corps \xe9taient assez marqu\xe9 cela. Je garderais ces marques \xe0 vie, tout comme vous et vos cicatrices"}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"C'est vrais qu'elle avait pens\xe9 \xe0 beaucoup de chose pour les cacher ou les faires dispara\xeetre mais les embellir .. Ca jamais. Au fond d'elle c'\xe9tait comme une force de les avoir, elle avait juste du mal \xe0 les accepter."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-13",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 17:11:28"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Bien que tr\xe8s g\xean\xe9e, la lapine me laisse d\xe9couvrir les marques sur son corps. Elle ose tout juste soutenir mon regard en \xe9tant ainsi expos\xe9e mais r\xe9ponds \xe0 mes interrogations. Je n'insiste pas plus, retirant ma main pour me r\xe9installer dans mon fauteuil et lui resservir un verre."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"J'aime. J'aime beaucoup. Et j'appr\xe9cie beaucoup que tu ais accept\xe9 de me les montrer, en particulier si je suis le premier \xe0 les voir. J'adorerais les relier avec un stylo ou m\xeame les l\xe9cher du bout de la langue. Tr\xe8s sensuelle comme d\xe9couverte."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Une fois le verre servit, je viens d\xe9boutonner mes boutons de manchettes. Je me l\xe8ve pour la retirer sans un mot et je viens me poster devant la lapine. Je suis tr\xe8s grand, elle non et ses bras n'\xe9tant pas extensible, je viens me mettre \xe0 genoux devant elle. Je la regarde en plissant les yeux."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je ne me met pas \xe0 genoux devant n'importe qui, saches le. Regarde moi, n'ait pas peur. Il n'y que toi et moi et une bouteille de whisky... Et peut \xeatre quelques autres."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je bombe le torse, posant mes mains sur le bout des accoudoirs de son fauteuil, l'enfermant dans une prison intime entre elle et moi. Comme moi, je la laisse d\xe9couvrir mon corps mais sans la moindre g\xe8ne. Je suis m\xeame pr\xeat \xe0 l'aider si vraiment la g\xe8ne est trop intense."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-13",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 17:39:16"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"L'observant se rassoir correctement, elle eu un rire en entendant la proposition qu'il faisait sur son corps. Il aurait du travail en plus, il n'avait d\xe9couvert que son ventre. M\xeame elle n'avait jamais eu l'id\xe9e de les tracer au stylos. Le remerciant d'un sourire quand il lui resservit un verre, elle ferait attention \xe0 ne pas le quiller cette fois ci. Par contre plus \xe7a allait et moins elle serait en capacit\xe9 de reprendre sa Harley pour rentrer chez elle. C'est \xe0 ce moment la qu'il prit la d\xe9cision de retirer sa chemise, et m\xeame si la lapine avait l\xe2cher son d\xe9bardeur,  celui ci tombant \xe0 peine \xe0 la moiti\xe9 de son ventre."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Ecarquillant les yeux quand il s'installa \xe0 genoux face \xe0 elle, avec les bras du blond, elle se sentait prise au pi\xe8ge. Mais une cage plut\xf4t agr\xe9able a vrai dire. Remercions ce merveilleux whiskey, elle eu le courage de venir d\xe9couvrir les cicatrices de son patron. D\xe9licatement elle posa le bout des doigts de sa main droite sur le torse avant de les suivre doucement. Certaines plus profondes que d'autres et quand on s'attardait quelque instant on en voyait des plus ancienne, ne laissant qu'une fine ligne blanche."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Si je continue \xe0 boire je ne serais plus capable de rentrer chez moi et je serais obliger de devoir louer une chambre ici. Apr\xe8s qui sait, peut-\xeatre que le patron de l'\xe9tablissement sera d'\xe2me charitable en me faisant une petite r\xe9duction sur une chambre ~"}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Peut \xe0 peut sa main se posa \xe0 plat sur son torse pour continuer de d\xe9couvrir celui-ci tout en observant le blond."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-14",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 17:55:10"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Oria fait de grands yeux en me voyant arriver torse nu. Je ne sais pas ce qui lui passe par la t\xeate en me voyant m'installer devant elle mais, assez rapidement, elle semble \xe0 l'aise et viens d'elle m\xeame d\xe9couvrir mes cicatrices. Des coupures plus ou moins profondes, des balles re\xe7ues, des \xe9clats de m\xe9tal. Toute une vie de mort grav\xe9es dans ma chaire et suivit par ses petits doigts."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Elle prend la parole pour me signaler qu'elle sent d\xe9j\xe0 qu'elle a trop bu pour rentrer, se demandant si je lui ferais un prix si elle reste dans une chambre. La demande est un peu \xe9tonnante mais certainement plus raisonnable aussi. Fron\xe7ant les sourcils avec une discr\xe8te grimace, je lui r\xe9ponds :"})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Allons, pas de \xe7a entre nous. Je ne te fais pas payer dans ce genre de cas. C'est de ma faute si tu n'es pas en \xe9tat de rentrer, ce serait de l'extorsion."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je lui souris, sentant sa main devenir plus s\xfbr, plus aventureuse, un r\xe9el contact de sa part. Je remarque d'ailleurs quelques vergetures encore visibles, son d\xe9bardeur \xe9tant encore l\xe9g\xe8rement relev\xe9 et, vu ma position je ne r\xe9siste pas \xe0 l'envie qui me titille depuis que je les ai vu. L'alcool n'aide en rien dans ces situations. Je me penche sur elle jusqu'\xe0 venir poser un petit baiser sur sa peau au niveau d'une de ses vergetures et, apr\xe8s un rapide regard, je viens d\xe9poser le bout de ma langue \xe0 la base de celle ci pour la remonter enti\xe8rement. C'est comme un jeu d'enfant et je pouffe de rire une fois termin\xe9."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"D\xe9sol\xe9, je n'ai pas pu r\xe9sister. C'est juste rigolo."}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-14",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 18:36:12"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Son pouce viendra \xe0 doucement caresser un impact de balle alors qu'elle continuait de l'observer de ses yeux vairons. Rigolant l\xe9g\xe8rement quand il viendra \xe0 dire que cette situation \xe9tait sa faute, elle \xe9tait techniquement aussi fautive que lui. En r\xe8gle g\xe9n\xe9ral elle faisait toujours attention quand elle prenait sa moto mais la situation avait quelque peu d\xe9rap\xe9 au moment ou il avait sortie cet alcool si bon."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Son autre main finira par se poser sur son \xe9paule, celle ci d\xe9couvrant plut\xf4t la masse musculaire. C'est la qu'elle le verra s'approcher d'elle et faire quelque chose qui la surpris. Embrasser et l\xe9cher une de ses cicatrices. Ses oreilles se redress\xe8rent d'un cou a cause de la surprise et on pouvait nettement entendre son pon-pon fr\xe9tiller \xe9nergiquement contre le cuir du fauteuil. A croire que son corps avait appr\xe9cier cette sensation."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Je .. Euh .. Mr Dyers .."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"La lapine perdait ses mots alors qu'elle semblait chercher une r\xe9ponse dans le regard de celui ci. Il \xe9tait maintenant si proche d'elle que \xe7a en \xe9tait d\xe9concertant."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-15",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 22:15:36"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Oria n'a pas chercher \xe0 me repousser et semble m\xeame avoir trouv\xe9 la chose agr\xe9able, bien que surprenante. Elle ne partage pas mon rire, me regardant d'un air tr\xe8s \xe9tonn\xe9. Je retrouve rapidement mon calme en comprenant, puis me redresse pour retrouver ma pause face \xe0 elle, encore \xe0 genoux."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Oui, un peu trop intime peut \xeatre. D\xe9sol\xe9, je... Je suis trop curieux parfois."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"C'est toujours compliqu\xe9 quand on est le leader de la ville de savoir \xe0 quel moment on partage un moment intime avec moi et \xe0 quel moment on le fait par peur des repr\xe9sailles. Je me redresse finalement et apr\xe8s un petit signe de la t\xeate, je vais retrouver ma place sans m\xeame m'emb\xeater \xe0 remettre ma chemise."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Et c'est pour cette raison que tu ne trouve pas de partenaire de couche ? Par peur de montrer les stigmates de ton pass\xe9 ? Et... Plus important encore : est-ce que me les montrer t'a aid\xe9 \xe0 mieux les supporter ?"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-15",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 23:06:20"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"La lapine continuait de la regarder avec une certaine incompr\xe9hension alors qu'il reprenait place dans son fauteuil. Il lui fallu quelques instant mais elle se remit bient\xf4t \xe0 rigoler l\xe9g\xe8rement. Buvant une gorg\xe9 de son alcool, elle regarda ensuite le liquide avec un petite sourire."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Ca aurait peut-\xeatre plus agr\xe9able pour vous avec une goutte de cet alcool sur mon ventre .."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Durant cette phrase elle \xe9tait perdu dans ses pens\xe9s avant de se souvenir ou elle \xe9tait et surtout avec qui elle \xe9tait. A l'heure actuel c'\xe9tait surtout ce breuvage qui lui permettait de les supporter. C'est la qu'elle r\xe9alisa ce qu'elle venait de dire. Est-ce qu'elle l'avait dit \xe0 voix haute ? Dans sa t\xeate ? En chuchotant ? La barbe ! Elle observa alors le visage de William en cherchant une r\xe9ponse \xe0 sa question."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-16",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"01-02-2023 23:43:05"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Comme j'ai retrouv\xe9 ma place, je retrouve aussi mon verre et m\xeame si la peau de la lapine avec un go\xfbt assez doux et agr\xe9able, il lutte difficilement contre l'ar\xf4me d'un whisky aussi cher. Oria imite mon geste puis observe le verre en pronon\xe7ant une phrase qui sonne comme une invitation."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je regarde Oria, tr\xe8s \xe9tonn\xe9 et apr\xe8s quelques instants, elle aussi sembla r\xe9aliser ce qu'elle venait de dire, tout aussi \xe9tonn\xe9e. Nous nous regardons sans rien dire, sans doute \xe0 se demander l'un comme l'autre si nous avons bien entendu la m\xeame chose. Je prend l'initiative de reprendre."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Tu veux que je l\xe8che tes vergetures apr\xe8s que tu y ait mise un peu d'alcool ? Moi je veux bien. En plus, vu qu'il y a des petits creux, le whisky resterait bien en place et tu pourrais guider ma langue ainsi. Ce n'est pas n\xe9cessairement une mauvaise id\xe9e... Enfin, si je me suis recul\xe9, c'est pour ne pas que tu sois g\xean\xe9e de me montrer ton corps. Si \xe7a t'a plus et \xe7a te tente, on peut voir s'il est meilleur comme \xe7a."}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-16",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"02-02-2023 00:52:02"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"En voyant la r\xe9action de son patron, la lapine comprit rapidement qu'elle avait dit \xe7a \xe0 voix haute et que du coup il l'avait clairement entendu. Rougissant de plus belle, elle d\xe9tourna le regard en passant une main dans ses cheveux court. Si elle continuait, qu'est ce que William allait penser d'elle ? En aucun cas elle ne voulait profiter de la situation de l'homme. Dans cette situation elle ne savait m\xeame plus si c'\xe9tait un moment patron-employ\xe9 ou de deux adultes consentant. Techniquement elle avait fini le travail donc on rentrait dans la seconde option et en plus l'alcool lui retirait un peu de sa timidit\xe9 et avec ce que William venait de faire .. Et m\xeame lui proposait a cause de ce qu'elle avait dit. Non clairement \xe7a n'aidait pas Oria \xe0 r\xe9fl\xe9chir avec autant de r\xe9flexion cas son habitude."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Excusez moi je ne sais pas ce qui ma prit en disant Mr Dyers .. Enfin William ? Je peux vous appelez William n'est ce pas ? Dans ma t\xeate c'est plus logique ainsi. Ce que je veux dire c'est je ne sais pas ci tout ceci est une bonne id\xe9e"}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Se relevant elle commen\xe7a a faire les cents pas dans le bureau tout en croisant un bras sous sa poitrine et en tenant son verre dans l'autre main, buvant une gorg\xe9 de ce doux liquide."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"C'est vrai que l'ambiance est propice, m\xeame si avec la chemin\xe9 allum\xe9 \xe7a serait mieux. Beaucoup de patron fantasme dans un sens sur leur secr\xe9taire, alors d'accord je n'ai pas ce poste la ici je ne suis qu'une simple responsable mais dans un sens \xe7a revient au m\xeame. Mais je n'ai clairement jamais eu ce genre de relation \xe0 mon travail. Apr\xe8s vous me direz on est \xe0 Langdorf, une ville ou chaque instant devrait se vivre \xe0 fond, vu qu'on peut se faire tirer dessus \xe0 chaque coin de rue. Mais chacune de me relations se sont passez exclusivement dans le noir \xe0 cause de mon corps, et \xe7a serait un sacril\xe8ge de ne pas pouvoir voir un ci bel homme au bord de l'extase"}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Finissant sa tirade, elle rebu un gorg\xe9 de son verre tout en ce demandant s'il avait seulement comprit ce qu'elle voulait dire. Car dans sa propre t\xeate, cela restait flous."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-17",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"02-02-2023 01:20:24"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Oria ne paru pas \xe9tonn\xe9 par ma r\xe9ponse mais plut\xf4t troubl\xe9e, comme si quelque chose n'allait pas. Elle s'excuse pour avoir propos\xe9 cela tout haut et ne semble plus vouloir r\xe9aliser cette id\xe9e. Je m'appr\xeate \xe0 lui dire que ce n'est pas grave, que je comprends, qu'il y aura surement d'autres occasions mais je la vois se lever soudain et commencer \xe0 faire les cents pas."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Elle commence alors \xe0 faire un monologue en semblant chercher un sens profond \xe0 mon geste, une signification cach\xe9e au rapport que cela implique entre nous. Je la suis du regard, tr\xe8s \xe9tonn\xe9, incapable de l'interrompre. Lorsqu'elle marque une pause et que je l\xe8ve le doigt, m'appr\xeatant \xe0 prendre la parole mais le moulin \xe0 parole reprends. Une seconde fois. Puis une troisi\xe8me. Finalement, elle s'interrompt et ne semble pas bien plus avanc\xe9e qu'auparavant."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Attends, j'ai rat\xe9 un wagon je crois. Ce n'est une forme de pr\xe9liminaire pour t'amener dans mon lit. Enfin je ne sais pas pour toi, mais je trouve juste l'id\xe9e de l\xe9cher ta peau \xe0 la fois sensuelle et amusante. Il n'y a pas un engagement \xe0 autre chose derri\xe8re. Au mieux un comparatif entre le whisky sur vergeture et le whisky sur cicatrice."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Si j'ai surement rat\xe9 des pans de la r\xe9flexion de la lapine, je n'ai pas rat\xe9 la partie concernant la chemin\xe9e. Je me l\xe8ve donc de ma chaise et vais allumer la dites chemin\xe9e. Pas de complications \xe0 base de buche \xe0 aller chercher, de feu \xe0 allumer, de trappe d'\xe9vacuation \xe0 ouvrir. J'appuie sur une simple bouton et FROUF, une flamme jaillis au centre de la chemin\xe9e, \xe9clairant la pi\xe8ce de mani\xe8re irr\xe9guli\xe8re. Je fais un signe de la t\xeate \xe0 Oria pour qu'elle revienne ici"})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Aller vient, allonge toi sur la peau de b... Bon sur le tapis devant la chemin\xe9e et montre moi o\xf9 tu as envie de sentir ma langue. On va voir comment il est meilleur ce whisky."}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-17",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"02-02-2023 07:46:48"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Elle avait bien vu son index se lever durant son monologue, comme un \xe9colier attendant sagement qu'on lui donne sa parole. Et heureusement qu'il ne l'avait pas coup\xe9 dans sa parole parce que \xe7a aurait vraiment mis le bazars dans sa t\xeate. Calmant sa respiration qui c'\xe9tait l\xe9g\xe8rement acc\xe9l\xe9r\xe9, \xe0 son tour elle l'\xe9couta tout en s'appuyant contre son bureau."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Sur le moment elle se sentait extr\xeamement stupide, son cerveau fonctionnait trop vite et du coup interpr\xe9tait tout de travers. Apr\xe8s on va pas se mentir que parler d'un potentiel mariage avec elle n'avait en rien aid\xe9 son cerveau. L'observant allum\xe9 la chemin\xe9, elle eu un petit sourire. Au moins dans sa tirade il avait relever un point. Elle viendra m\xeame \xe0 l\xe9g\xe8rement rire \xe0 l'\xe9vocation d'une potentiel peau de b\xeate qui n'\xe9tait pas pr\xe9sente. Observant son verre quelques instant, cherchant surement une r\xe9ponse dans l'alcool, la lapine se d\xe9tacha du bureau pour s'approcher de William. Etrangement, m\xeame avec de l'alcool dans le sang, son russe \xe9tait encore bon."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.em,{children:"- Ce whiskey va me mener \xe0 ma perte."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Elle viendra \xe0 s'assoir devant le feu, le regardant en souriant avant de reprendre une petite gorg\xe9. La gorg\xe9 qui lui donna assez de courage pour retirer son d\xe9bardeur rouge. D\xe9voilant ainsi une magnifique lingerie rouge aussi, avec petit charme dor\xe9 entre ses seins. D\xe9j\xe0 juste \xe7a c'\xe9tait une \xe9tape pour elle alors qu'elle levait les yeux vers le blond, les oreilles plaquer sur le c\xf4t\xe9."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.em,{children:"- En esp\xe9rant que ce ne soit pas du g\xe2chis .."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-18",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"02-02-2023 19:15:16"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Les yeux d'Oria suivent mon mouvement et j'arrive m\xeame \xe0 lui arracher un rire quand elle me voit r\xe9aliser qu'il n'y a pas de peau de b\xeate sur le sol. Je crois que pour l'un comme pour l'autre, l'alcool commence \xe0 faire son oeuvre. La lapine vient vers moi en reprenant en russe cette fois et viens s'asseoir pr\xe8s du feu, souriante. Je m'installe moi aussi, ressentant d\xe9j\xe0 la chaleur du foyer maintenant allum\xe9."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Apr\xe8s avoir repris une gorg\xe9e de whisky, la jeune femme retire son d\xe9bardeur avant m\xeame que je le r\xe9alise. Avec un regard tr\xe8s intimid\xe9, elle me regarde en coin en restant sur le russe et semble bien d\xe9cid\xe9e \xe0 tenter l'exp\xe9rience. Je fais un sourire et pose une main sur son genou tout proche pour la rassurer"})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.em,{children:"russe - Ne t'en fais pas, pour moi aussi c'est une premi\xe8re mais je serais doux. Si \xe7a ne va pas, tu aura juste \xe0 me le dire."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je me rapproche un peu de mon employ\xe9e, lui retirant le verre des mains pour le poser au sol. Je viens la prendre par les \xe9paules et la pousse d\xe9licatement sur le tapis derri\xe8re elle pour l'y allonger. Beaucoup plus pratique \xe0 mon sens, m\xeame si j'imagine facilement qu'elle souhaite se retourner un moment. Puis, je ram\xe8ne le verre pr\xe8s de sa main droite, non sans y avoir tremper le bout de l'index."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.em,{children:"russe - permets que je commence"})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Sans attendre de r\xe9ponse ou d'approbation, je passe mon doigt imbib\xe9 d'alcool sur une de ses vergetures puis me penche sur elle pour venir la l\xe9cher de bas en haut, appuyant toute ma langue sur cette marque de peau. Je la suis, sentant le m\xe9lange d'alcool et de la peau de la lapine. Loin d'\xeatre mauvais. Je me redresse ensuite avec un sourire et la regarde, attendant de voir o\xf9 elle souhaite amener le jeu."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-18",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"02-02-2023 22:28:58"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"La responsable observa du coin de l'oeil son patron s'installer \xe0 ses c\xf4t\xe9s avec un petit sourire. Sa main sur son genoux viendra la rassurer et la mettre un peu plus en confiance. William \xe9tait des plus d\xe9licats avec elle, et sa voix paraissait plus .. sensuelle quand il parlait dans sa langue natale. A moins que ce ne soit l'alcool qui lui fasse penser cela ?"})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Doucement il viendra \xe0 lui prendre son verre des mains avant de l'allonger sur le sol. Elle aurait pus avoir froid dans cette tenue mais la chemin\xe9 la r\xe9chauffait, ainsi que le whiskey qui coulait dans son corp. Les jambes l\xe9g\xe8rement repli\xe9, son ventre plat et toute ses vergetures \xe0 la vue du blond. Par reflexe elle viendra poser ses mains sur sa poitrine pour la cacher un peu tout en l'observant dans les yeux."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Hochant timidement la t\xeate quand il viendra \xe0 commencer, elle rentra l\xe9g\xe8rement le ventre quand il d\xe9posa la premi\xe8re goute d'alcool qui coula le long de sa cicatrices. La sensation \xe9tait froide mais elle fut rapidement remplacer par la langue beaucoup plus chaude du blond. Un couinement se fit l\xe9g\xe8rement entendre, ainsi que le frottement fr\xe9n\xe9tique de son pon-pon contre le tapis."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.em,{children:"- A .. Alors .. ?"})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Les rougeurs \xe9taient toujours pr\xe9sente sur ses joues et elle r\xeavait a ce moment la de se cacher avec ses oreilles mais la vision qu'elle avait \xe9tait beaucoup trop belle pour \xe7a."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-19",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"02-02-2023 23:11:17"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"La situation est pour le moins sensuelle. Une jolie femme et moi, allong\xe9s devant une chemin\xe9e avec assez d'alcool dans le sang pour ne plus ressentir d'inhibition. Ma langue qui passe sur sa peau, lentement, longuement. Elle a le go\xfbt du whisky avec... Un fond de noisette ou d'amande je dirais."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Heureusement que je suis tr\xe8s doux avec elle car la lapine est encore tr\xe8s tendue. Ses mains cachant ses seins, ses jambes repli\xe9es, pr\xeate \xe0 se mettre en boule et ce petit couinement des plus adorable lorsque ma langue est entr\xe9 en contact avec sa peau... Pourtant, maintenant au dessus d'elle, je vois le bout de sa petite queue remuer et elle trouve le courage de me demander mon avis. Je fais mine de r\xe9fl\xe9chir"})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Hum... Difficile \xe0 dire. C'\xe9tait bon mais je pense qu'il faut confirmer la chose plusieurs fois pour se faire un r\xe9el avis."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Je trempe \xe0 nouveau mon doigt dans son verre tout en parlant et reproduit mon geste sur une autre vergeture pr\xe8s de son flanc droit. A nouveau, mon doigt suis sa peau, suivi pas ma langue. Je sens du bout de ma langue le creux form\xe9 par la vergeture, chose qui me fait sourire et une fois au bout de celle ci, je remue un peu ma langue sur place avant d'y poser un bisou."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Hum, vraiment pas mal, meilleur m\xeame. A toi cette fois, laquelle je dois essayer ensuite ?"}),"\n"]}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"oria-levenski--responsable-des-esclaves-19",children:"Oria Levenski ~ Responsable des esclaves"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"02-02-2023 23:52:36"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"William avait une lueur dans son regard qui n'\xe9tait pas la avant qu'elle ne se retrouva allonger. Elle avait l'impression que le russe trouvait cette situation .. Amusante ? Ou bien excitante ? Elle ne savait pas vraiment sur le moment. N\xe9anmoins ses rougeur \xe9taient toujours pr\xe9sente."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"A sa question ou il fit mine de r\xe9fl\xe9chir, elle rigola l\xe9g\xe8rement. Enfaite son patron \xe9tait un joueur, il connaissait d\xe9j\xe0 la r\xe9ponse et voulait juste prendre plaisir \xe0 recommencer. Elle d\xe9plia timidement sa jambe droite alors que son corps frissonnait sous sa langue. Ce qui la surpris par contre c'\xe9tait son petit bisou, tendre et en m\xeame temps adorable."})}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Il ne faudrait pas trop y prendre gout alors .."}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Alors qu'elle parlait, elle avait retirer la main poser sur son sein gauche. Son propre verre \xe9tant pos\xe9 non loin d'elle, Oria viendra y tremper son index avant de suivre une de ses vergeture proche de son nombril."})}),"\n",(0,r.jsx)(s.hr,{}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.h4,{id:"william-dyers-20",children:"William Dyers"}),"\n",(0,r.jsxs)(s.blockquote,{children:["\n",(0,r.jsx)(s.p,{children:"03-02-2023 00:11:36"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.strong,{children:"Le second coup de langue est mieux accept\xe9 et j'ai l'impression que la lapine commence d\xe9j\xe0 \xe0 mieux l'accepter. Elle rit a mes pointes d'humour, le laisse l\xe9cher son ventre et commence tout juste \xe0 se d\xe9tendre. Comprenant que je voulais vraiment qu'elle participe au jeu, elle abandonne le masquage d'un de ses seins pour venir tremper son doigt dans le whisky et le passer sur une de ses vergetures comme je l'ai fais."})}),"\n",(0,r.jsx)(s.p,{children:(0,r.jsx)(s.em,{children:"russe - Continue comme \xe7a et je ne boirais plus mon whisky autrement."})}),"\n",(0,r.jsx)(s.p,{children:"**Je pouffe de rire de ma propre phrase. Le simple fait de parler russe m'amuse mais je me reprend rapidement avant que le liquide ne s'\xe9carte. Je commence m\xeame un peu plus bas cette fois, ma langue partant d'au dessus de son pubis, \xe0 la fronti\xe8re de son pantalon et remontant doucement vers son nombril. Je suis le chemin qu'elle m'a d\xe9sign\xe9 du bout de la langue et lorsque j'arrive \xe0 son nombril, j'en fais le tour avant de reprendre le chemin trac\xe9 sur sa peau. Je me redresse avec un sourire **"}),"\n",(0,r.jsxs)(s.ul,{children:["\n",(0,r.jsx)(s.li,{children:"Le looping autour du nombril ajoute beaucoup. Comment \xe7a se passe pour toi, c'est agr\xe9able aussi ?"}),"\n"]})]})}function c(e={}){const{wrapper:s}={...(0,i.R)(),...e.components};return s?(0,r.jsx)(s,{...e,children:(0,r.jsx)(d,{...e})}):d(e)}},28453:(e,s,n)=>{n.d(s,{R:()=>l,x:()=>t});var r=n(96540);const i={},a=r.createContext(i);function l(e){const s=r.useContext(a);return r.useMemo((function(){return"function"==typeof e?e(s):{...s,...e}}),[s,e])}function t(e){let s;return s=e.disableParentContext?"function"==typeof e.components?e.components(i):e.components||i:l(e.components),r.createElement(a.Provider,{value:s},e.children)}}}]);