"use strict";(self.webpackChunkarchive_dernier_verre=self.webpackChunkarchive_dernier_verre||[]).push([[52282],{51230:(e,n,s)=>{s.r(n),s.d(n,{assets:()=>o,contentTitle:()=>l,default:()=>c,frontMatter:()=>i,metadata:()=>t,toc:()=>u});var r=s(74848),a=s(28453);const i={title:"2023-12-25 - Balcon (Anna, Mark, Oria)",toc_min_heading_level:4,toc_max_heading_level:4,tags:["tag_AnnaClark","tag_MarkLegrand","tag_OriaLevenski"]},l="Balcon (Anna, Mark, Oria)",t={id:"Archives/Events/ReceptionFinAnnee2023/Balcon2_Anna_Mark_Oria",title:"2023-12-25 - Balcon (Anna, Mark, Oria)",description:"Balcon - 25/12/2023",source:"@site/docs/Archives/Events/ReceptionFinAnnee2023/Balcon2_Anna_Mark_Oria.mdx",sourceDirName:"Archives/Events/ReceptionFinAnnee2023",slug:"/Archives/Events/ReceptionFinAnnee2023/Balcon2_Anna_Mark_Oria",permalink:"/ArchiveLeDernierVerre/docs/Archives/Events/ReceptionFinAnnee2023/Balcon2_Anna_Mark_Oria",draft:!1,unlisted:!1,tags:[{inline:!1,label:"Anna Clark",permalink:"/ArchiveLeDernierVerre/docs/tags/AnnaClark",description:"Archives o\xf9 Anna Clark intervient"},{inline:!1,label:"Mark Legrand",permalink:"/ArchiveLeDernierVerre/docs/tags/MarkLegrand",description:"Archives o\xf9 Mark Legrand intervient"},{inline:!1,label:"Oria Levenski",permalink:"/ArchiveLeDernierVerre/docs/tags/OriaLevenski",description:"Archives o\xf9 Oria Levenski intervient"}],version:"current",frontMatter:{title:"2023-12-25 - Balcon (Anna, Mark, Oria)",toc_min_heading_level:4,toc_max_heading_level:4,tags:["tag_AnnaClark","tag_MarkLegrand","tag_OriaLevenski"]}},o={},u=[{value:"Balcon - 25/12/2023",id:"balcon---25122023",level:3},{value:"Anna Clark",id:"anna-clark",level:4},{value:"Oria Levenski",id:"oria-levenski",level:4},{value:"Anna Clark",id:"anna-clark-1",level:4},{value:"Oria Levenski",id:"oria-levenski-1",level:4},{value:"Anna Clark",id:"anna-clark-2",level:4},{value:"Oria Levenski",id:"oria-levenski-2",level:4},{value:"Mark Legrand",id:"mark-legrand",level:4},{value:"Anna Clark",id:"anna-clark-3",level:4},{value:"Oria Levenski",id:"oria-levenski-3",level:4},{value:"Anna Clark",id:"anna-clark-4",level:4},{value:"Oria Levenski",id:"oria-levenski-4",level:4},{value:"Anna Clark",id:"anna-clark-5",level:4},{value:"Mark Legrand",id:"mark-legrand-1",level:4},{value:"Anna Clark",id:"anna-clark-6",level:4},{value:"Mark Legrand",id:"mark-legrand-2",level:4},{value:"Anna Clark",id:"anna-clark-7",level:4},{value:"Mark Legrand",id:"mark-legrand-3",level:4},{value:"Anna Clark",id:"anna-clark-8",level:4},{value:"Mark Legrand",id:"mark-legrand-4",level:4},{value:"Anna Clark",id:"anna-clark-9",level:4},{value:"Mark Legrand",id:"mark-legrand-5",level:4},{value:"Anna Clark",id:"anna-clark-10",level:4}];function d(e){const n={admonition:"admonition",blockquote:"blockquote",h1:"h1",h3:"h3",h4:"h4",header:"header",hr:"hr",li:"li",p:"p",strong:"strong",ul:"ul",...(0,a.R)(),...e.components};return(0,r.jsxs)(r.Fragment,{children:[(0,r.jsx)(n.header,{children:(0,r.jsx)(n.h1,{id:"balcon-anna-mark-oria",children:"Balcon (Anna, Mark, Oria)"})}),"\n",(0,r.jsx)(n.admonition,{title:"R\xe9ception de fin d'ann\xe9e 2023",type:"info",children:(0,r.jsx)(n.h3,{id:"balcon---25122023",children:"Balcon - 25/12/2023"})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"25-12-2023 22:10:43"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna Clark, v\xeatue d'une robe rouge, fit son entr\xe9e majestueuse dans la salle de r\xe9ception du manoir. Ses talons tintent sur le sol, attirant l'attention de quelques personnes pr\xe9sentes. Son regard froid scrutait la pi\xe8ce, analysant chaque visage, chaque mouvement. Un sourire subtil se dessina sur ses l\xe8vres tout en saluant quelques personnes."})}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"D'un geste rapide, Anna prit un verre de champagne d'un plateau port\xe9 par un esclave du march\xe9, indiff\xe9rente \xe0 la condition de ce dernier. Elle se dirigea vers le balcon qui offrait une vue imprenable sur les jardins du manoir. L'air froid de la nuit caressait son visage, mais Anna \xe9tait imperm\xe9able \xe0 celui-ci."})}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Arriv\xe9e au balcon, elle prit une profonde inspiration, laissant ses yeux divaguer sur les jardins."})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"oria-levenski",children:"Oria Levenski"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"25-12-2023 22:32:54"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"A cette soir\xe9e, si quelqu'un pouvait rivaliser avec l'\xe9l\xe9gance d'Anna, c'\xe9tait bien Oria. Malgr\xe9 son ventre de femme enceinte, notre anglaise se d\xe9pla\xe7ait sans aucune g\xeane sur ses talons haut. Venu au bras de William, elle l'avait laiss\xe9 \xe0 l'int\xe9rieur quand elle avait aper\xe7ut madame Wingate se diriger vers le balcon. Attrapant une coupe de cocktail sans alcool, grossesse oblige, elle ira \xe0 son tour sur le balcon pour aller la saluer."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Bonsoir Anna, qu'elle magnifique soir\xe9e vous ne trouvez pas ?"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark-1",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"25-12-2023 22:52:54"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna, perch\xe9e sur le balcon, tourna l\xe9g\xe8rement la t\xeate en entendant la voix d'Oria. Son regard se posa sur la jeune femme enceinte, et un sourire presque bienveillant \xe9tira ses l\xe8vres rouge vif."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsxs)(n.li,{children:["Oria, ",(0,r.jsx)(n.strong,{children:"dit-elle, sa voix basse et mesur\xe9e,"})," quelle surprise agr\xe9able de vous voir ici ce soir. Magnifique soir\xe9e, en effet,",(0,r.jsx)(n.strong,{children:"ajouta-t-elle d'un ton calme."})]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna leva son verre de champagne en direction d'Oria, un geste qui transcendait les apparences festives de la soir\xe9e."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Je suppose que vous m'avez rejoint ici pour autre chose que les paillettes, le champagne et bien \xe9videmment ma pr\xe9sence."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle prit une gorg\xe9e du liquide dor\xe9, ses yeux ne quittant pas Oria."})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"oria-levenski-1",children:"Oria Levenski"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"25-12-2023 23:16:31"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"C'\xe9tait rare aussi que Oria puisse regarder une femme dans les yeux sans avoir a les baisser. Les deux jeunes femmes faisaient quasiment la m\xeame taille. S'approchant doucement d'elle, elle leva a son tour sa coupe pour l'imiter avant de poser celle ci sur la rambarde du balcon."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"En r\xe8gle g\xe9n\xe9rale je n'aime pas parler travail durant ce genre de r\xe9ception mais je dois en profiter avant que mon lapereau en d\xe9cide autrement."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle caressa l\xe9g\xe8rement son ventre d'une main avant de reprendre sa coupe pour boire une gorg\xe9 et enfin reprendre."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Je ne peux pas vivre \xe9ternellement au crochet de William c'est une \xe9vidence. Est ce que votre proposition d'emploi tien toujours ?"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark-2",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"26-12-2023 20:32:34"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna, son sourire persistant, \xe9couta attentivement les mots d'Oria. Un \xe9clat de satisfaction \xe9tincela dans ses yeux \xe0 la mention de la proposition d'emploi. Des \xe9trangers vinrent\r\nd\xe9ranger la tranquillit\xe9 de leur discutions mais elle retourna son regard satisfait vers Oria."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Mon offre tient toujours, peut-\xeatre m\xeame plus que jamais. Travailler pour le march\xe9 des esclaves n'est pas simplement un emploi, c'est une opportunit\xe9 de gravir des \xe9chelons et de prosp\xe9rer. Je parle bien sur au sein de la Cosa Nostra. Vous ne serez pas un simple membre mais vous d\xe9buterez en tant que sous-chef externe de celle-ci. Externe car, vous g\xe9rerez un march\xe9 pour moi et vous aurez la protection ainsi que certain droits sur la mafia sans pour autant en faire enti\xe8rement partie pour l'instant."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna fit quelques pas, contemplant les jardins en contrebas."})}),"\n",(0,r.jsx)(n.p,{children:"-Vous avez du potentiel, et je ne parle pas seulement de vos talents dans la gestion du march\xe9 des esclaves. Je parle de pouvoir r\xe9el, de contr\xf4le, comme toutes les femmes. Et si l'envie et l'ambition vous prend vous pourrez faire de grande chose."}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle planta son regard dans celui d'Oria."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Travailler pour moi et pour le march\xe9 des esclaves signifie que vous ne serez plus dans l'ombre de William. Vous aurez votre propre influence, vos propres r\xe9ussites. Mais cela requiert une loyaut\xe9 in\xe9branlable envers la Cosa Nostra."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"oria-levenski-2",children:"Oria Levenski"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"28-12-2023 23:43:50"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Quand des nouvelles personnes arriv\xe8rent sur le balcon, une de ses oreilles viendra \xe0 se tourner vers eux pour capter leur conversation. Rien de bien int\xe9ressant et Anna \xe9tait bien plus int\xe9ressante que ces deux inconnus. Prenant une gorg\xe9 de son cocktail sans alcool, elle \xe9couta la proposition que sa potentiel futur patronne \xe9tait entrain de lui faire."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Votre proposition est int\xe9ressante il est vrai, n\xe9anmoins je ne suis pas en qu\xeate de contr\xf4le et de pouvoir. Surtout avec une vie de maman qui m'attend, je vais devoir apprendre \xe0 lever un peu le pied au niveau du travail et prendre du temps pour ma famille."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Caressant son ventre d'une main, la lapine planta \xe0 son tour son regard si particulier dans ceux de l'humaine. N\xe9anmoins elle eu un petite rire quand Anna lui annaon\xe7a qu'elle \xe9tait dans l'ombre de William. Oria \xe9tait juste une personne d'un naturel timide et qui n'aimait tout simplement pas se mettre en avant. Par contre, pour la futur m\xe8re, apprendre \xe0 lever le pied ne voulait pas dire en faire le moins possible, simplement rentrer chez elle tout les soirs \xe0 des heures descentes et ne plus rapporter de travail chez elle."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Comprenez aussi que ma famille passera toujours avant la Cosa Nostra, et que je ne veux pas qu'il y ai des tension dans mon foyer si vos hommes et ceux de William sont amener \xe0 se croiser."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"mark-legrand",children:"Mark Legrand"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"29-12-2023 09:52:08"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Le c\xe9l\xe8bre Mark Legrand fait une entr\xe9e dans le balcon apr\xe8s avoir trouv\xe9 sa Patronne. Une rose \xe0 la main gauche comme d'habitude, et son verre de champagne \xe0 moiti\xe9 plein dans l'autre, et le cigare aux l\xe8vres bien-s\xfbr ! Il reste \xe0 environ cinq pas des femmes en silence et quand Anna regardera Mark, il viendra faire un signe de t\xeate pour qu'elle comprenne que c'est une information importante qui l'attend."})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark-3",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"29-12-2023 11:05:45"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna, r\xe9ceptive \xe0 la r\xe9ponse d'Oria, \xe9couta avec une attention particuli\xe8re. Ses yeux ne quitt\xe8rent pas ceux de la jeune femme, sondant chaque nuance de ses paroles."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsxs)(n.li,{children:["La famille avant tout, un choix respectable,",(0,r.jsx)(n.strong,{children:"commenta-t-elle, son sourire persistant."})," Je suis dispos\xe9e \xe0 accommoder vos priorit\xe9s familiales, Oria. Apr\xe8s tout, dans notre monde, le pouvoir ne se mesure pas seulement \xe0 la sph\xe8re professionnelle."]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:"Anna inclina l\xe9g\xe8rement la t\xeate, acceptant tacitement les conditions \xe9nonc\xe9es."}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Je comprend et respecterons vos limites. Tant que vous restez loyale \xe0 la Cosa Nostra, nous pouvons trouver un \xe9quilibre qui vous convienne. Vous aurez en plus notre protection contre votre loyaut\xe9."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Lorsque Mark Legrand fit son entr\xe9e, Anna d\xe9via son regard vers lui. Le signe de t\xeate discret lui indiquait une affaire importante. D'un geste, elle fit signe \xe0 Mark d'attendre quelques instant."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Alors Oria, tout est bon pour vous ?"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"oria-levenski-3",children:"Oria Levenski"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"29-12-2023 15:10:00"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"La jeune femme fut soulager de voir qu'Anna comprenait ses priorit\xe9s. Allier une vie de famille \xe0 une vie de mafieux n'\xe9tait pas commun dans cette ville mais elle savait qu'elle allait y arriver, surtout que William \xe9tait la pour l'\xe9pauler."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Et comme je l'ai dis par le pass\xe9 durant mon entretien avec feu Reza Camille, je ne divulguerais pas d'information sur la Cosa Nostra, la Bratva ou m\xeame le Dernier Verre du temps ou je travaillais l\xe0-bas."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"A nouveau, l'un de ses longues oreilles noirs se tourna vers l'origine d'un nouveau bruit. En remarquant le hochement de t\xeate d'Anna, elle se risqua \xe0 tourner l\xe9g\xe8rement pour voir de qui il s'agissait avant de remarquer Mark. C'est vrai qu'il \xe9tait de retour en ville, mais elle ne savait pas qu'il \xe9tait retourner \xe0 la Cosa Nostra. Ce serait ironique qu'il se retrouve \xe0 nouveau \xe0 travailler sous ses ordres si lui aussi est affecter \xe0 l'esclavage. Reportant son regard sur sa futur patronne, elle lui tendra la main, comme pour officialiser leur accord."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Tout cela me semble clair sinon Anna, je serais heureuse de travailler pour vous."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark-4",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"29-12-2023 21:52:35"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna, satisfaite de l'accord qui se dessinait, prit la main d'Oria dans la sienne d'un geste ferme. Un \xe9clat de victoire passa dans ses yeux."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsxs)(n.li,{children:["Tout est clair alors,",(0,r.jsx)(n.strong,{children:"d\xe9clara-t-elle."})," Je suis certaine que votre contribution au march\xe9 des esclaves et \xe0 la Cosa Nostra sera des plus pr\xe9cieuses."]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle rel\xe2cha la main d'Oria et reprit la parole."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Nous conviendrons d'un autre rendez-vous pour parler des r\xe8gles au sein de la Cosa Nostra ainsi que votre petit rituel de passage. Sur ceux, je vous lib\xe8re votre soir\xe9e."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"oria-levenski-4",children:"Oria Levenski"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"30-12-2023 00:57:52"}),"\n"]}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Avec plaisir Anna. Je vous souhaite donc une bonne fin de soir\xe9e."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle lui adressa un dernier sourire avant de s'\xe9loigner de la femme. Elle salua Mark d'un signe de t\xeate avant de retourner \xe0 l'int\xe9rieur se r\xe9chauffer, et surtout, trouver quelqu'un en particulier."})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark-5",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"30-12-2023 19:49:41"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna, observant Oria s'\xe9loigner avec un sentiment de contentement, resta sur le balcon un moment, absorb\xe9e par les lumi\xe8res de la ville nocturne. Une pens\xe9e fugace traversa son esprit : les alliances se formaient et se brisaient, mais elle restait inchang\xe9e dans son r\xf4le de chef de la Cosa Nostra."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsxs)(n.li,{children:["Mark,",(0,r.jsx)(n.strong,{children:"appela-t-elle, son attention se reportant vers le commandant de sa mafia."})," Parlons de ce dont vous m'aavez fait signe."]}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"mark-legrand-1",children:"Mark Legrand"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"30-12-2023 20:22:18"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Il s'approche donc de Anna doucement en venant ce mettre \xe0 sa droite, face au balcon afin d'admirer la vue. D\xe9posant la rose sur la rambarde devant elle et sa coupe de champagne sur sa droite afin d'avoir les mains libres."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"J'ai fait la rencontre avec les motards. Vlad est un gars r\xe9glo et j'laime bien. Il est neutre avec tout le monde sauf DedSec qu'il veut d\xe9truire. J'ai fait part de ma vision sur eux et il semble \xeatre d'accord avec moi. Les Yakuzas, ont fait une demande d'alliance pour nous abattre. Les Motards refusent mais livrent simplement des armes. La mafia polyn\xe9sienne est \xe9galement contre nous."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Il reprend son souffle et observe sa patronne du coin de l'oeil en laissant en nuage de vanille sortir de sa bouche."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Il est tr\xe8s ouvert \xe0 une alliance.  \xc9galement ouvert \xe0 la discussion avec vous. Comme j'avais pas id\xe9e de quoi vous vouliez comme contrat j'ai juste t\xe2ter le terrain. J'esp\xe8re que \xe7a vous va ?"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark-6",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"30-12-2023 20:42:58"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna, se tenant sur le balcon, \xe9couta attentivement les informations que Mark partageait. Son regard per\xe7ant analysait chaque d\xe9tail, \xe9valuant les opportunit\xe9s et les menaces qui se dessinaient dans les alliances et les rivalit\xe9s entre les diff\xe9rentes factions."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsxs)(n.li,{children:["Vlad et les Motards peuvent \xeatre de pr\xe9cieux alli\xe9s dans notre lutte contre DedSec,",(0,r.jsx)(n.strong,{children:"commenta-t-elle, son esprit d\xe9j\xe0 en train de formuler des plans."})," Leur neutralit\xe9 peut \xeatre notre force. Quant aux Yakuzas et la mafia polyn\xe9sienne..."]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle fit une pause, r\xe9fl\xe9chissant \xe0 la meilleure approche."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Nous devons \xeatre prudents dans nos d\xe9cisions. L'alliance avec les Motards semble prometteuse, mais je veux en savoir plus sur leurs intentions r\xe9elles. Je vais aller rencontrer Vlad, et m'assurer surtout qu'il comprenne que tout partenariat avec la Cosa Nostra \xe0 ses conditions et que nous soyons sur la m\xeame longueur d'onde."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna prit une gorg\xe9e de champagne, son regard fix\xe9 sur la ville en contrebas."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Quant aux Yakuzas, surveillons-les de pr\xe8s. Je ne veux pas de surprises. Je vous donne une mission, Dante, et vous,  au moindre coup bas des Yakuzas, rendez-le-leur au centuple comme vous paraissez si bien savoir le faire. Pour Dedsec, je vais en faire mon affaire. J'ai quelques id\xe9es en t\xeate, mais nous en parlerons quand nous serons dans un endroit plus calme et discret."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle tourna l\xe9g\xe8rement la t\xeate vers Mark."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Et pour la mafia polyn\xe9sienne, il faudra r\xe9colter le plus d'informations possible. Il peut y avoir des opportunit\xe9s que nous n'avons pas encore envisag\xe9es. Nous devons \xeatre en avance sur eux, toujours."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"mark-legrand-2",children:"Mark Legrand"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"30-12-2023 21:13:18"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Il bouge la t\xeate de haut en bas en \xe9coutant les nouvelles missions. D\xe9posant son cigare devant Anna pour lui offrir le reste qui est \xe0 50%. Buvant son verre de champagne avant de dire."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsxs)(n.li,{children:["\n",(0,r.jsx)(n.p,{children:"Aussi, les motards veulent que nous limitons les violences abusives et inutiles sur les esclaves. C'est leurs conditions."}),"\n"]}),"\n",(0,r.jsxs)(n.li,{children:["\n",(0,r.jsx)(n.p,{children:"Je prends note des nouvelles missions. Les jaloux semblent \xeatre \xe0 nos pieds. La Cosa Nostra est puissante, faudrait leurs faire comprendre comment s'exprimer en parlant aux grands. J'ai tr\xe8s h\xe2te de pouvoir mettre une petite tape sur les doigts !"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Dira t'il avec un petit rire et son sourire carnassier."})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark-7",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"30-12-2023 21:24:58"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna, recevant le cigare de Mark et \xe9coutant ses paroles, arborait un l\xe9ger sourire. Elle tourna ensuite son regard vers la rose qu'il venait de lui offrir et posa son verre pour la r\xe9cup\xe9rer et la fixer."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsxs)(n.li,{children:["Les conditions des Motards ne sont pas d\xe9raisonnables,",(0,r.jsx)(n.strong,{children:"dit-elle, prenant ensuite le cigare qu'il lui offrait."})," Limiter les violences inutiles est une demande que nous pouvons respecter sans compromettre notre position de pouvoir. Cela peut m\xeame jouer en notre faveur, montrant que la Cosa Nostra peut \xeatre r\xe9ceptive aux n\xe9gociations. Mais j'ai n\xe9anmoins une certaine remarque , il faut qu'ils sachent que la Cosa Nostra n'accepte aucune violence gratuite. Mais, comme toujours, la discipline doit \xeatre maintenue. Si certains m\xe9ritent une correction, qu'ils la re\xe7oivent. Nous ne tol\xe9rerons pas la moindre remise en question de notre autorit\xe9."]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle prit le cigare en bouche d'un geste assur\xe9, laissant la fum\xe9e s'\xe9lever dans l'air nocturne."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Pour ce qui est des jaloux, Mark, je suis d'accord. Il est temps de rappeler \xe0 tous qui d\xe9tient un grand pouvoir dans cette ville. Qu'ils apprennent \xe0 parler aux grands, comme vous dites si bien. Mais, veillons \xe0 ce que nos actions soient calcul\xe9es et strat\xe9giques. Nous ne voulons pas de repr\xe9sailles impr\xe9vues."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna observa les jardins sous un nouvel angle, la fum\xe9e du cigare se m\xealant aux lumi\xe8res de la nuit."})}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle prit une nouvelle gorg\xe9e de champagne, son regard fix\xe9 sur l'horizon nocturne. Les jeux de pouvoir continuaient, et Anna \xe9tait pr\xeate \xe0 jouer toutes les cartes pour assurer la supr\xe9matie de la Cosa Nostra."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"La puissance r\xe9side dans la surprise et la ruse, pas dans la brutalit\xe9 aveugle. Je vous donne des cartes, \xe0 vous de rentrer dans le jeu et d'\xeatre l'un de mes pions sur mon jeu d'\xe9chec et de d\xe9placer les v\xf4tres parfaitement."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"mark-legrand-3",children:"Mark Legrand"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"30-12-2023 21:40:58"}),"\n"]}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"C'est ce que je leurs ait dis aux Motards. Vous \xeates pas dans le genre \xe0 frapper pour rien et nous corrigeons juste ceux qui m\xe9rite. Il comprend tout cela bien-s\xfbr. Vlad me ressemble un peu et je pense que nous pouvons \xeatre des solides alli\xe9s."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Il observe de nouveau dans le coin de l'oeil Anna."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Une grande puissance qui n'h\xe9site pas \xe0 faire preuve de violence aveugle, il n'y a rien de plus monstrueux. Ca fais tr\xe8s peur et beaucoup r\xe9fl\xe9chir quand la prochaine fois la violence aveugle devient n\xe9gociation. D'ailleurs vous qui parlez de pion, vous me voyez comme lequel ? Cavaleir, fou, tour, pion ?"}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark-8",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"30-12-2023 21:51:34"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna, \xe9coutant les paroles de Mark sur la violence aveugle, arborait un air impassible, mais une lueur de d\xe9saccord \xe9tincelait dans ses yeux."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"La violence aveugle est une arme brute, mais elle manque de finesse. Elle peut inspirer la peur, certes, mais elle ne garantit pas la loyaut\xe9 ni la respectabilit\xe9. La n\xe9gociation et la manipulation sont des outils bien plus subtils et puissants. Nous ne devons pas agir comme des sauvages, mais comme des strat\xe8ges implacables."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle inspira profond\xe9ment, laissant la fum\xe9e du cigare s'\xe9chapper de ses l\xe8vres avec \xe9l\xe9gance."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"La peur est un moyen de contr\xf4le, mais l'intelligence et la ruse sont des moyens de domination durables."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Quant \xe0 la question de Mark sur son r\xf4le dans le jeu, elle sourit, lan\xe7ant un regard per\xe7ant \xe0 son commandant."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Mark, vous \xeates mon fou. Impr\xe9visible, audacieux, capable de se d\xe9placer dans des directions inattendues. Vous \xeates l'atout sur lequel je compte pour surprendre nos adversaires. Soyez mon fou, Mark, et jouez ce r\xf4le avec ma\xeetrise."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"mark-legrand-4",children:"Mark Legrand"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"31-12-2023 05:29:11"}),"\n"]}),"\n"]}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Ce que je veux dire, c'est que us\xe9 de violence brute permet d'\xeatre plus impactant apr\xe8s pour une n\xe9gociation, car nous avons prouv\xe9 notre force, et une personne remplie de peur va \xeatre heureuse de n\xe9gocier afin d'\xe9viter la guerre. Enfin bon, c'est votre vision qui est importante."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Il viendra mettre le dos contre la rambarde et croise ensuite les bras en observant les gens qui dansent et profite de cette belle r\xe9ception."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Un fou ? Je ne sais pas comment le prendre."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Dira t'il avec un petit rire surpris."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"En tout cas j'aime bien l'id\xe9e."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Il craque sa nuque fortement avant de regarder sa Patronne."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Savez- vous si les Yakuzas sont \xe0 notre bal ? Que je puisse m'amuser un peu, verbalement en tout cas."}),"\n"]}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark-9",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"31-12-2023 13:39:05"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna, \xe9coutant les arguments de Mark sur l'impact de la violence brute dans les n\xe9gociations, resta impassible. Sa vision strat\xe9gique demeurait in\xe9branlable."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"La peur peut inciter \xe0 la n\xe9gociation, mais elle engendre \xe9galement la m\xe9fiance et le ressentiment. Notre objectif est d'asseoir notre pouvoir de mani\xe8re \xe0 ce que la n\xe9gociation soit in\xe9vitable, mais choisie par ceux qui nous font face. Nous avons des points de vu bien diff\xe9rents mais cela reste la m\xeame choses d'un certain c\xf4t\xe9."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle s'appuya l\xe9g\xe8rement \xe0 son tour contre la rambarde, observant la foule de la r\xe9ception qui continuait \xe0 danser et c\xe9l\xe9brer."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Quant \xe0 votre r\xf4le de fou, Mark, prenez-le comme un compliment. Les fous dans un jeu d'\xe9checs peuvent se mouvoir de mani\xe8re inattendue, d\xe9routant l'adversaire"}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"La question sur la pr\xe9sence des Yakuzas la fit sourire l\xe9g\xe8rement."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Oui, ils sont ici ce soir. Vous n'avez pas entendu l'annonce faites il y a quelques temps ? Faites preuve de votre charme verbal, mais veillez \xe0 ne pas d\xe9voiler trop de nos cartes. Profitez de la soir\xe9e \xe0 notre avantage."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna reprit son cigare, laissant la fum\xe9e s'\xe9lever dans l'air nocturne. Les jeux de pouvoir continuaient, et elle savait que chaque mouvement devait \xeatre calcul\xe9 pour maintenir la supr\xe9matie de la Cosa Nostra."})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"mark-legrand-5",children:"Mark Legrand"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"01-01-2024 19:32:35"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Il \xe9couta sa patronne en d\xe9saccord avec sa vision des choses. \xc7a ne l'\xe9tonne pas et pr\xe9f\xe8re ne pas y rajouter quoique ce soit. Finissant son champagne."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"J'entends tellement de chose vous savez. J'irai voir leurs chef en esp\xe9rant qu'il est int\xe9ressant."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Il d\xe9colle son dos du mur en d\xe9posant son verre vide sur la rambarde."})}),"\n",(0,r.jsx)(n.hr,{}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.h4,{id:"anna-clark-10",children:"Anna Clark"}),"\n",(0,r.jsxs)(n.blockquote,{children:["\n",(0,r.jsx)(n.p,{children:"02-01-2024 20:07:18"}),"\n"]}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna sourit l\xe9g\xe8rement, s'amusant de la d\xe9cision de Mark de rencontrer les Yakuzas."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Allez, Mark, amusez-vous bien."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Elle r\xe9cup\xe9ra le verre vide de Mark et la rose qu'il lui avait offerte, les tenant d\xe9licatement entre ses doigts. Elle lui tendit son cigare avec un l\xe9ger sourire."})}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Je vous rend ceci. Profitez de la soir\xe9e, Mark. Je vais all\xe9e m'amuser un peu moi aussi dans cette f\xeate. Nous nous retrouverons bient\xf4t."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:(0,r.jsx)(n.strong,{children:"Anna inclina l\xe9g\xe8rement la t\xeate avant de s'\xe9loigner dans la foule."})})]})}function c(e={}){const{wrapper:n}={...(0,a.R)(),...e.components};return n?(0,r.jsx)(n,{...e,children:(0,r.jsx)(d,{...e})}):d(e)}},28453:(e,n,s)=>{s.d(n,{R:()=>l,x:()=>t});var r=s(96540);const a={},i=r.createContext(a);function l(e){const n=r.useContext(i);return r.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function t(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(a):e.components||a:l(e.components),r.createElement(i.Provider,{value:n},e.children)}}}]);